 #include <iostream>
#include <vector>

int main() {
  // Static array (fixed size at compile time)
  int static_array[5] = {10, 20, 30, 40, 50};

  // Accessing elements of the static array
  std::cout << "Static array elements: ";
  for (int i = 0; i < 5; ++i) {
    std::cout << static_array[i] << " ";
  }
  std::cout << std::endl;

  // Dynamic array (size can change at runtime using std::vector)
  std::vector<int> dynamic_array;

  // Adding elements to the dynamic array
  dynamic_array.push_back(100);
  dynamic_array.push_back(200);
  dynamic_array.push_back(300);

  // Accessing elements of the dynamic array using index
  std::cout << "Dynamic array elements (using index): ";
  for (size_t i = 0; i < dynamic_array.size(); ++i) {
    std::cout << dynamic_array[i] << " ";
  }
  std::cout << std::endl;

  // Accessing elements of the dynamic array using iterators
  std::cout << "Dynamic array elements (using iterators): ";
  for (auto it = dynamic_array.begin(); it != dynamic_array.end(); ++it) {
    std::cout << *it << " ";
  }
  std::cout << std::endl;

  // Inserting an element at a specific position in the dynamic array
  dynamic_array.insert(dynamic_array.begin() + 1, 150); // Insert 150 at index 1

  std::cout << "Dynamic array after insertion: ";
    for (int element : dynamic_array) {
        std::cout << element << " ";
    }
    std::cout << std::endl;

  // Deleting an element from the dynamic array
  dynamic_array.erase(dynamic_array.begin() + 2); // Erase element at index 2

  std::cout << "Dynamic array after deletion: ";
    for (int element : dynamic_array) {
        std::cout << element << " ";
    }
    std::cout << std::endl;

  // Getting the size of the dynamic array
  std::cout << "Size of dynamic array: " << dynamic_array.size() << std::endl;

  return 0;
}

