// A simple C++ implementation of a singly linked list
#include <iostream>

// Node class representing a node in the linked list
class Node {
public:
    int data;
    Node* next;

    // Constructor to initialize a node with data
    Node(int data) : data(data), next(nullptr) {}
};

// LinkedList class to manage the linked list
class LinkedList {
public:
    Node* head;

    // Constructor to initialize an empty linked list
    LinkedList() : head(nullptr) {}

    // Function to insert a new node at the beginning of the list
    void insertAtBeginning(int data) {
        Node* newNode = new Node(data);
        newNode->next = head;
        head = newNode;
    }

    // Function to insert a new node at the end of the list
    void insertAtEnd(int data) {
        Node* newNode = new Node(data);
        if (head == nullptr) {
            head = newNode;
            return;
        }
        Node* current = head;
        while (current->next != nullptr) {
            current = current->next;
        }
        current->next = newNode;
    }

    // Function to delete a node with a given key
    void deleteNode(int key) {
        Node* temp = head, *prev = nullptr;

        // If head node itself holds the key to be deleted
        if (temp != nullptr && temp->data == key) {
            head = temp->next;  // Changed head
            delete temp;         // free old head
            return;
        }

        // Search for the key to be deleted, keep track of the
        // previous node as we need to change 'prev->next'
        while (temp != nullptr && temp->data != key) {
            prev = temp;
            temp = temp->next;
        }

        // If key was not present in linked list
        if (temp == nullptr) return;

        // Unlink the node from linked list
        prev->next = temp->next;

        delete temp;  // Free memory
    }

    // Function to print the linked list
    void printList() {
        Node* current = head;
        while (current != nullptr) {
            std::cout << current->data << " ";
            current = current->next;
        }
        std::cout << std::endl;
    }

    // Function to free memory allocated to the linked list
    ~LinkedList() {
        Node* current = head;
        while (current != nullptr) {
            Node* next = current->next;
            delete current;
            current = next;
        }
        head = nullptr; // Reset head after freeing memory
    }
};

int main() {
    // Create a linked list
    LinkedList myList;

    // Insert elements at the beginning
    myList.insertAtBeginning(3);
    myList.insertAtBeginning(2);
    myList.insertAtBeginning(1);

    // Insert an element at the end
    myList.insertAtEnd(4);

    std::cout << "Linked list elements: ";
    myList.printList(); // Output: 1 2 3 4

    // Delete a node
    myList.deleteNode(2);

    std::cout << "Linked list after deleting 2: ";
    myList.printList(); // Output: 1 3 4

    return 0;
}

