// Stack Implementation using C++
#include <iostream>
#include <stack>

using namespace std;

int main() {
    // Creating a stack of integers
    stack<int> myStack;

    // Pushing elements onto the stack
    myStack.push(10);
    myStack.push(20);
    myStack.push(30);

    cout << "Stack elements: ";
    // Accessing the top element without removing it
    cout << "Top element: " << myStack.top() << endl;

    // Displaying the stack's size
    cout << "Stack size: " << myStack.size() << endl;

    // Popping elements from the stack (LIFO)
    cout << "Popping elements: ";
    while (!myStack.empty()) {
        cout << myStack.top() << " "; // Display the top element
        myStack.pop(); // Remove the top element
    }
    cout << endl;

    // Checking if the stack is empty after popping all elements
    if (myStack.empty()) {
        cout << "Stack is now empty." << endl;
    } else {
        cout << "Stack is not empty." << endl;
    }

    // Example use case: Parenthesis Matching (Simplified)
    string expression = "({[]})"; // Valid expression
    stack<char> parenthesisStack;
    bool balanced = true;

    for (char c : expression) {
        if (c == '(' || c == '{' || c == '[') {
            parenthesisStack.push(c);
        } else if (c == ')' || c == '}' || c == ']') {
            if (parenthesisStack.empty()) {
                balanced = false;
                break;
            }

            char top = parenthesisStack.top();
            parenthesisStack.pop();

            if ((c == ')' && top != '(') || (c == '}' && top != '{') || (c == ']' && top != '[')) {
                balanced = false;
                break;
            }
        }
    }

    if (!parenthesisStack.empty()) {
        balanced = false; //Handles cases like "(("
    }

    if (balanced) {
        cout << "Parenthesis in the expression are balanced." << endl;
    } else {
        cout << "Parenthesis in the expression are not balanced." << endl;
    }

    return 0;
}

