  while (!myQueue.empty()) {
    cout << myQueue.front() << " "; // Print the element being dequeued
    myQueue.pop(); // Remove the front element
  }
  cout << endl;

  // Check if the queue is empty
  if (myQueue.empty()) {
    cout << "Queue is now empty." << endl;
  } else {
