// C++ implementation of Binary Search
#include <iostream>
#include <vector>

using namespace std;

// Function to perform binary search on a sorted vector
int binarySearch(const vector<int>& arr, int target) {
    int low = 0;
    int high = arr.size() - 1;

    // Continue searching while the low index is less than or equal to the high index
    while (low <= high) {
        // Calculate the middle index (to avoid overflow, use low + (high - low) / 2)
        int mid = low + (high - low) / 2;

        // If the target is found at the middle index, return the index
        if (arr[mid] == target) {
            return mid;
        }

        // If the target is smaller than the middle element, search in the left half
        else if (arr[mid] > target) {
            high = mid - 1;
        }

        // If the target is larger than the middle element, search in the right half
        else {
            low = mid + 1;
        }
    }

    // If the target is not found, return -1
    return -1;
}

int main() {
    // Example usage
    vector<int> sortedArray = {2, 5, 8, 12, 16, 23, 38, 56, 72, 91};
    int targetValue = 23;

    // Perform binary search
    int resultIndex = binarySearch(sortedArray, targetValue);

    // Print the result
    if (resultIndex == -1) {
        cout << "Target value not found in the array." << endl;
    } else {
        cout << "Target value found at index: " << resultIndex << endl;
    }

    return 0;
}

