#include <iostream>
#include <vector>

using namespace std;

// Function to perform Bubble Sort
void bubbleSort(vector<int>& arr) {
  int n = arr.size();
  bool swapped;
  for (int i = 0; i < n - 1; i++) {
    swapped = false; // Optimization: Check if any swaps occurred in this pass
    for (int j = 0; j < n - i - 1; j++) {
      // Compare adjacent elements
      if (arr[j] > arr[j + 1]) {
        // Swap if they are in the wrong order
        swap(arr[j], arr[j + 1]);
        swapped = true; // Mark that a swap occurred
      }
    }

    // If no two elements were swapped in inner loop,
    // the array is sorted
    if (swapped == false)
      break;
  }
}

// Function to print a vector
void printVector(const vector<int>& arr) {
  for (int i = 0; i < arr.size(); i++) {
    cout << arr[i] << " ";
  }
  cout << endl;
}

int main() {
  vector<int> arr = {64, 34, 25, 12, 22, 11, 90};

  cout << "Unsorted array: ";
  printVector(arr);

  bubbleSort(arr); // Sort the array

  cout << "Sorted array: ";
  printVector(arr);

  return 0;
}

