// Insertion Sort in C++
#include <iostream>
#include <vector>

void insertionSort(std::vector<int>& arr) {
  int n = arr.size();
  for (int i = 1; i < n; i++) {
    int key = arr[i]; // The element to be inserted into the sorted sequence
    int j = i - 1; // Index of the last element in the sorted sequence

    // Move elements of arr[0..i-1], that are greater than key,
    // to one position ahead of their current position
    while (j >= 0 && arr[j] > key) {
      arr[j + 1] = arr[j];
      j = j - 1;
    }
    arr[j + 1] = key; // Insert the key into its correct position
  }
}

// Function to print the array
void printArray(const std::vector<int>& arr) {
  for (int element : arr) {
    std::cout << element << " ";
  }
  std::cout << std::endl;
}

int main() {
  std::vector<int> arr = {12, 11, 13, 5, 6}; // Example array
  std::cout << "Array before sorting: \n";
  printArray(arr);

  insertionSort(arr); // Call the insertion sort function

  std::cout << "Array after sorting: \n";
  printArray(arr);

  return 0;
}

