 #include <iostream>
#include <fstream>
#include <vector>
#include <algorithm>

using namespace std;

// Function to split the input file into chunks that fit in memory
void splitFile(const string& inputFile, const string& baseOutputFile, size_t chunkSize) {
    ifstream in(inputFile, ios::binary);
    if (!in.is_open()) {
        cerr << "Error: Could not open input file." << endl;
        return;
    }

    size_t chunkId = 0;
    vector<int> chunk;
    int value;

    while (in >> value) {
        chunk.push_back(value);
        if (chunk.size() == chunkSize) {
            sort(chunk.begin(), chunk.end());
            string outputFile = baseOutputFile + to_string(chunkId++) + ".txt";
            ofstream out(outputFile, ios::binary);
            if (!out.is_open()) {
                cerr << "Error: Could not open output file " << outputFile << endl;
                return;
            }
            for (int val : chunk) {
                out << val << " ";
            }
            out.close();
            chunk.clear();
        }
    }

    // Handle the last chunk (if any)
    if (!chunk.empty()) {
        sort(chunk.begin(), chunk.end());
        string outputFile = baseOutputFile + to_string(chunkId) + ".txt";
        ofstream out(outputFile, ios::binary);
        if (!out.is_open()) {
            cerr << "Error: Could not open output file " << outputFile << endl;
            return;
        }
        for (int val : chunk) {
            out << val << " ";
        }
        out.close();
    }
    in.close();
}

// Function to merge two sorted files
void mergeFiles(const string& inputFile1, const string& inputFile2, const string& outputFile) {
    ifstream in1(inputFile1, ios::binary);
    ifstream in2(inputFile2, ios::binary);
    ofstream out(outputFile, ios::binary);

    if (!in1.is_open() || !in2.is_open() || !out.is_open()) {
        cerr << "Error: Could not open input or output files for merging." << endl;
        return;
    }

    int value1, value2;
    bool hasValue1 = (in1 >> value1);
    bool hasValue2 = (in2 >> value2);

    while (hasValue1 && hasValue2) {
        if (value1 <= value2) {
            out << value1 << " ";
            hasValue1 = (in1 >> value1);
        } else {
            out << value2 << " ";
            hasValue2 = (in2 >> value2);
        }
    }

    // Copy remaining elements from inputFile1 (if any)
    while (hasValue1) {
        out << value1 << " ";
        hasValue1 = (in1 >> value1);
    }

    // Copy remaining elements from inputFile2 (if any)
    while (hasValue2) {
        out << value2 << " ";
        hasValue2 = (in2 >> value2);
    }

    in1.close();
    in2.close();
    out.close();
}

// External Merge Sort function
void externalMergeSort(const string& inputFile, const string& outputFile, size_t chunkSize) {
    string baseOutputFile = "temp_chunk_";

    // Phase 1: Split and Sort Chunks
    splitFile(inputFile, baseOutputFile, chunkSize);

    // Phase 2: Merge Chunks
    size_t chunkId = 0;
    while (true) {
        string inputFile1 = baseOutputFile + to_string(chunkId++) + ".txt";
        string inputFile2 = baseOutputFile + to_string(chunkId++) + ".txt";
        string mergedFile = "temp_merged_" + to_string(chunkId / 2) + ".txt";

        ifstream checkFile1(inputFile1);
        ifstream checkFile2(inputFile2);

        if (!checkFile1.good()) {
            //If the first chunk doesn't exist, then merging is complete.
            if (chunkId==1) {
               ifstream lastChunk (baseOutputFile + "0.txt");
               ofstream finalOutput(outputFile);
               finalOutput << lastChunk.rdbuf();
               finalOutput.close();
               lastChunk.close();
            }

            // Clean up temporary files. (left as an exercise to the reader!)
            return;
        }


        if (!checkFile2.good()) {
            //If only one file remains, copy to the merged file (renaming).

            ifstream lastChunk(inputFile1);
            ofstream mergedOutput(mergedFile);
            mergedOutput << lastChunk.rdbuf();
            lastChunk.close();
            mergedOutput.close();


        } else {
            mergeFiles(inputFile1, inputFile2, mergedFile);
        }

        checkFile1.close();
        checkFile2.close();


        //Rename mergedFile to inputFile1.
        remove(inputFile1.c_str()); //Delete temporary files
        remove(inputFile2.c_str());

        if (checkFile2.good()) {
            rename(mergedFile.c_str(), inputFile1.c_str());
            chunkId = chunkId - 1;  //Start from beginning
        } else {
            rename(mergedFile.c_str(), outputFile.c_str());

            //Copy the result to outputFile.  This avoids an extra rename if only one file remains.
        } 

        if(chunkId > 100000) { //Infinite loop protection
            cerr << "Stopping at max iterations.";
            return;   //break out of loop in case of error
        }

        //Check if there are files other than outputFile.
    }


}

int main() {
    // Example usage
    string inputFile = "input.txt";
    string outputFile = "output.txt";
    size_t chunkSize = 3; // Adjust chunk size based on available memory

    // Create a sample input file
    ofstream input(inputFile, ios::binary);
    input << "5 2 8 1 9 4 7 3 6 0";
    input.close();

    externalMergeSort(inputFile, outputFile, chunkSize);

    cout << "External Merge Sort completed. Output in " << outputFile << endl;

    return 0;
}

merge-sort.code:
 #include <iostream>
#include <vector>

// Function to merge two sorted subarrays into a single sorted subarray
void merge(std::vector<int>& arr, int left, int mid, int right) {
    int n1 = mid - left + 1; // Size of the left subarray
    int n2 = right - mid;    // Size of the right subarray

    // Create temporary arrays to hold the subarrays
    std::vector<int> L(n1), R(n2);

    // Copy data to temporary arrays L[] and R[]
    for (int i = 0; i < n1; i++)
        L[i] = arr[left + i];
    for (int j = 0; j < n2; j++)
        R[j] = arr[mid + 1 + j];

    // Merge the temporary arrays back into arr[left..right]
    int i = 0, j = 0, k = left;
    while (i < n1 && j < n2) {
        if (L[i] <= R[j]) {
            arr[k] = L[i];
            i++;
        } else {
            arr[k] = R[j];
            j++;
        }
        k++;
    }

    // Copy the remaining elements of L[], if there are any
    while (i < n1) {
        arr[k] = L[i];
        i++;
        k++;
    }

    // Copy the remaining elements of R[], if there are any
    while (j < n2) {
        arr[k] = R[j];
        j++;
        k++;
    }
}

// Recursive function to implement Merge Sort
void mergeSort(std::vector<int>& arr, int left, int right) {
    if (left < right) {
        // Find the middle point
        int mid = left + (right - left) / 2;

        // Recursively sort the first and second halves
        mergeSort(arr, left, mid);
        mergeSort(arr, mid + 1, right);

        // Merge the sorted halves
        merge(arr, left, mid, right);
    }
}

int main() {
    std::vector<int> arr = {12, 11, 13, 5, 6, 7};
    int n = arr.size();

    std::cout << "Unsorted array: \n";
    for (int i = 0; i < n; i++)
        std::cout << arr[i] << " ";
    std::cout << "\n";

    mergeSort(arr, 0, n - 1);

    std::cout << "Sorted array: \n";
    for (int i = 0; i < n; i++)
        std::cout << arr[i] << " ";
    std::cout << "\n";

    return 0;
}

