// C++ implementation of QuickSort
#include <iostream>
#include <vector>
#include <algorithm> // For std::swap and std::random_shuffle
#include <random>

using namespace std;

// Function to partition the array and return the partition index
int partition(vector<int>& arr, int low, int high) {
    // Choose the pivot (in this case, the last element)
    int pivot = arr[high];

    // Index of smaller element and indicates the right position for pivot found so far
    int i = (low - 1);

    for (int j = low; j <= high - 1; j++) {
        // If the current element is smaller than or equal to the pivot
        if (arr[j] <= pivot) {
            i++; // increment index of smaller element
            swap(arr[i], arr[j]);
        }
    }
    swap(arr[i + 1], arr[high]);
    return (i + 1);
}

// Function to implement QuickSort
void quickSort(vector<int>& arr, int low, int high) {
    if (low < high) {
        // pi is partitioning index, arr[pi] is now at right place 
        int pi = partition(arr, low, high);

        // Separately sort elements before partition and after partition
        quickSort(arr, low, pi - 1);
        quickSort(arr, pi + 1, high);
    }
}

// Function to print an array
void printArray(const vector<int>& arr) {
    for (int value : arr) {
        cout << value << " ";
    }
    cout << endl;
}

// Function to shuffle the array to avoid worst case performance on sorted/nearly sorted arrays
void shuffleArray(vector<int>& arr) {
    random_device rd;
    mt19937 g(rd());
    shuffle(arr.begin(), arr.end(), g);
}

int main() {
    vector<int> arr = {10, 7, 8, 9, 1, 5};
    int n = arr.size();

    cout << "Unsorted array: \n";
    printArray(arr);

    //Shuffle the array for better average case performance
    shuffleArray(arr);

    quickSort(arr, 0, n - 1);

    cout << "Sorted array: \n";
    printArray(arr);

    return 0;
}

