// Counting Sort Implementation
#include <iostream>
#include <vector>

using namespace std;

// Function to perform counting sort
void countingSort(vector<int>& arr) {
  // Find the maximum element to determine the range
  int maxElement = 0;
  for (int num : arr) {
    if (num > maxElement) {
      maxElement = num;
    }
  }

  // Initialize the count array with size maxElement + 1 and all elements set to 0
  vector<int> count(maxElement + 1, 0);

  // Count the occurrences of each element in the input array
  for (int num : arr) {
    count[num]++;
  }

  // Modify the count array to store the cumulative counts
  for (int i = 1; i <= maxElement; ++i) {
    count[i] += count[i - 1];
  }

  // Create an output array to store the sorted elements
  vector<int> output(arr.size());

  // Place elements into the output array in sorted order
  for (int i = arr.size() - 1; i >= 0; --i) {
    output[count[arr[i]] - 1] = arr[i];
    count[arr[i]]--;
  }

  // Copy the sorted elements from the output array back to the input array
  for (int i = 0; i < arr.size(); ++i) {
    arr[i] = output[i];
  }
}

int main() {
  vector<int> arr = {4, 2, 2, 8, 3, 3, 1};

  cout << "Unsorted array: ";
  for (int num : arr) {
    cout << num << " ";
  }
  cout << endl;

  countingSort(arr);

  cout << "Sorted array: ";
  for (int num : arr) {
    cout << num << " ";
  }
  cout << endl;

  return 0;
}

