// Radix Sort Implementation in C++
#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

// Function to get the digit at a specific position (from right to left)
int getDigit(int num, int place) {
    int divisor = 1;
    for (int i = 0; i < place; ++i) {
        divisor *= 10;
    }
    return (num / divisor) % 10;
}

// Radix Sort Function
void radixSort(vector<int>& arr) {
    // Find the maximum number to determine the number of digits
    int maxVal = *max_element(arr.begin(), arr.end());

    // Count the number of digits in the maximum value
    int numDigits = 0;
    while (maxVal > 0) {
        maxVal /= 10;
        numDigits++;
    }

    // Perform counting sort for each digit, starting from the least significant digit
    for (int place = 0; place < numDigits; ++place) {
        // Create a count array to store the frequency of each digit (0-9)
        vector<int> count(10, 0);

        // Count the occurrences of each digit at the current place
        for (int num : arr) {
            int digit = getDigit(num, place);
            count[digit]++;
        }

        // Update the count array to store the cumulative counts
        for (int i = 1; i < 10; ++i) {
            count[i] += count[i - 1];
        }

        // Create a temporary output array
        vector<int> output(arr.size());

        // Build the output array in reverse order to maintain stability
        for (int i = arr.size() - 1; i >= 0; --i) {
            int digit = getDigit(arr[i], place);
            output[count[digit] - 1] = arr[i];
            count[digit]--;
        }

        // Copy the output array back to the original array
        arr = output;
    }
}

int main() {
    vector<int> arr = {170, 45, 75, 90, 802, 24, 2, 66};

    cout << "Unsorted array: ";
    for (int num : arr) {
        cout << num << " ";
    }
    cout << endl;

    radixSort(arr);

    cout << "Sorted array: ";
    for (int num : arr) {
        cout << num << " ";
    }
    cout << endl;

    return 0;
}

