 #include <iostream>
#include <vector>
#include <string>

// Simple hash function (for demonstration purposes only)
size_t hash_function(const std::string& key, size_t capacity) {
    size_t hash = 5381; // Initial hash value
    for (char c : key) {
        hash = ((hash << 5) + hash) + c; // hash * 33 + c
    }
    return hash % capacity; // Ensure hash is within the table's bounds
}

// Hash Table Implementation with Separate Chaining
template <typename K, typename V>
class HashTable {
private:
    std::vector<std::vector<std::pair<K, V>>> table; // The hash table itself (vector of linked lists)
    size_t capacity; // The number of buckets in the hash table
    size_t size; // The number of key-value pairs stored in the hash table

public:
    // Constructor
    HashTable(size_t capacity) : capacity(capacity), size(0) {
        table.resize(capacity);
    }

    // Insert a key-value pair into the hash table
    void insert(const K& key, const V& value) {
        size_t index = hash_function(key, capacity);
        table[index].push_back({key, value});
        size++;
    }

    // Retrieve the value associated with a given key
    V* get(const K& key) {
        size_t index = hash_function(key, capacity);
        for (auto& pair : table[index]) {
            if (pair.first == key) {
                return &pair.second;
            }
        }
        return nullptr; // Key not found
    }

    // Delete a key-value pair from the hash table
    void remove(const K& key) {
        size_t index = hash_function(key, capacity);
        for (auto it = table[index].begin(); it != table[index].end(); ++it) {
            if (it->first == key) {
                table[index].erase(it);
                size--;
                return;
            }
        }
    }

    // Get the current size of the hash table
    size_t getSize() const {
        return size;
    }

    // Get the capacity of the hash table
    size_t getCapacity() const {
        return capacity;
    }

    // Function to print the hash table (for debugging purposes)
    void print() const {
        for (size_t i = 0; i < capacity; ++i) {
            std::cout << "Bucket " << i << ": ";
            for (const auto& pair : table[i]) {
                std::cout << "(" << pair.first << ", " << pair.second << ") ";
            }
            std::cout << std::endl;
        }
    }
};

int main() {
    // Example Usage
    HashTable<std::string, int> hashTable(10); // Create a hash table with a capacity of 10

    hashTable.insert("apple", 1);
    hashTable.insert("banana", 2);
    hashTable.insert("cherry", 3);
    hashTable.insert("date", 4);

    std::cout << "Hash Table Size: " << hashTable.getSize() << std::endl;

    int* value = hashTable.get("banana");
    if (value != nullptr) {
        std::cout << "Value for banana: " << *value << std::endl;
    } else {
        std::cout << "Key 'banana' not found."
                  << std::endl;  // Fix: Added missing endl
    }

    hashTable.remove("banana");
    std::cout << "Hash Table Size after removing banana: " << hashTable.getSize() << std::endl;

    value = hashTable.get("banana");
    if (value != nullptr) {
        std::cout << "Value for banana: " << *value << std::endl;
    } else {
        std::cout << "Key 'banana' not found."
                  << std::endl; // Fix: Added missing endl
    }

    hashTable.print();

    return 0;
}

