// C++ implementation of Binary Search
#include <iostream>
#include <vector>

// Function to perform binary search
int binarySearch(const std::vector<int>& arr, int target) {
    int low = 0;
    int high = arr.size() - 1;

    while (low <= high) {
        int mid = low + (high - low) / 2; // Prevent potential overflow

        if (arr[mid] == target) {
            return mid; // Target found at index mid
        } else if (arr[mid] < target) {
            low = mid + 1; // Search in the right half
        } else {
            high = mid - 1; // Search in the left half
        }
    }

    return -1; // Target not found
}

int main() {
    std::vector<int> sortedArray = {2, 5, 8, 12, 16, 23, 38, 56, 72, 91};
    int targetValue = 23;

    int result = binarySearch(sortedArray, targetValue);

    if (result == -1) {
        std::cout << "Element is not found in array" << std::endl;
    } else {
        std::cout << "Element is found at index: " << result << std::endl;
    }

    targetValue = 50; // Test with a value not present
    result = binarySearch(sortedArray, targetValue);

     if (result == -1) {
        std::cout << "Element is not found in array" << std::endl;
    } else {
        std::cout << "Element is found at index: " << result << std::endl;
    }

    return 0;
}

