// A simple example of mathematical recursion: calculating factorial
#include <iostream>

// Recursive function to calculate factorial of a number n
// Factorial(n) = n * Factorial(n-1) for n > 0
// Factorial(0) = 1 (base case)
unsigned long long factorial(int n) {
    // Base case: if n is 0, return 1
    if (n == 0) {
        return 1;
    }
    // Recursive step: multiply n by the factorial of n-1
    else if (n > 0){
        return n * factorial(n - 1);
    }
    else {
        std::cerr << "Error: Cannot compute factorial of negative number" << std::endl;
        return 0; // Or throw an exception
    }
}

// Another example: Fibonacci sequence
// F(n) = F(n-1) + F(n-2) for n > 1
// F(0) = 0, F(1) = 1 (base cases)

unsigned long long fibonacci(int n) {
    if (n <= 1) {
        return n;
    }
    return fibonacci(n - 1) + fibonacci(n - 2);
}

int main() {
    int num = 5;
    std::cout << "Factorial of " << num << " is: " << factorial(num) << std::endl;
    std::cout << "Fibonacci of " << num << " is: " << fibonacci(num) << std::endl;
    return 0;
}

