// Backtracking example: Generating all permutations of a string
#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

// Function to generate permutations of a string using backtracking
void permute(string str, int l, int r, vector<string>& result) {
    // Base case: When l and r are equal, it means we have reached a leaf node
    // and a permutation is complete
    if (l == r) {
        result.push_back(str);
    } else {
        // Try all possible swaps at each position
        for (int i = l; i <= r; i++) {
            // Swap the current character with str[l]
            swap(str[l], str[i]);

            // Recursively generate permutations for the remaining part of the string
            permute(str, l + 1, r, result);

            // Backtrack: Swap back to restore the original string.  This is crucial
            // for exploring other possibilities.
            swap(str[l], str[i]);
        }
    }
}

int main() {
    string str = "ABC";
    vector<string> permutations;
    int n = str.length();

    // Call the permute function to generate all permutations
    permute(str, 0, n - 1, permutations);

    // Print the permutations
    cout << "Permutations of " << str << " are:" << endl;
    for (const string& permutation : permutations) {
        cout << permutation << endl;
    }

    return 0;
}

