// Two Pointer technique example: Find if a sorted array contains a pair with a given sum
#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

/*
  This function uses the two-pointer technique to find if a sorted array
  contains a pair of elements whose sum is equal to a given target sum.

  Parameters:
    arr: A sorted vector of integers.
    targetSum: The target sum to find.

  Returns:
    True if a pair with the target sum is found, false otherwise.
*/
bool hasPairWithSum(const vector<int>& arr, int targetSum) {
  // Check if the array is empty
  if (arr.empty()) {
    return false;
  }

  // Initialize two pointers, one at the beginning and one at the end of the array.
  int left = 0;
  int right = arr.size() - 1;

  // Iterate until the two pointers meet.
  while (left < right) {
    // Calculate the current sum of the elements at the two pointers.
    int currentSum = arr[left] + arr[right];

    // If the current sum is equal to the target sum, return true.
    if (currentSum == targetSum) {
      return true;
    } else if (currentSum < targetSum) {
      // If the current sum is less than the target sum, move the left pointer to the right.
      left++;
    } else {
      // If the current sum is greater than the target sum, move the right pointer to the left.
      right--;
    }
  }

  // If no pair with the target sum is found, return false.
  return false;
}

int main() {
  vector<int> arr = {2, 7, 11, 15};
  int targetSum = 9;

  if (hasPairWithSum(arr, targetSum)) {
    cout << "Array contains a pair with sum " << targetSum << endl;
  } else {
    cout << "Array does not contain a pair with sum " << targetSum << endl;
  }

  vector<int> arr2 = {1, 2, 3, 4, 5};
  int targetSum2 = 10;

  if (hasPairWithSum(arr2, targetSum2)) {
        cout << "Array contains a pair with sum " << targetSum2 << endl;
  } else {
        cout << "Array does not contain a pair with sum " << targetSum2 << endl;
  }

  return 0;
}

