// Function to find a pair in a sorted array that sums up to a target value using the two pointer technique.
bool findPairSum(vector<int>& arr, int target) {
    // Check if the array is empty or has fewer than two elements.
    if (arr.empty() || arr.size() < 2) {
        return false;
    }

    // Initialize two pointers, one at the beginning and one at the end of the array.
    int left = 0;
    int right = arr.size() - 1;

    // Iterate until the pointers meet.
    while (left < right) {
        // Calculate the sum of the elements pointed to by the two pointers.
        int sum = arr[left] + arr[right];

        // If the sum is equal to the target, we have found a pair.
        if (sum == target) {
            cout << "Pair found: (" << arr[left] << ", " << arr[right] << ")" << endl;
            return true;
        }
        // If the sum is less than the target, move the left pointer to the right to increase the sum.
        else if (sum < target) {
            left++;
        }
        // If the sum is greater than the target, move the right pointer to the left to decrease the sum.
        else {
            right--;
        }
    }

    // If no pair is found, return false.
    cout << "Pair not found." << endl;
    return false;
}

