// Sliding Window Example: Find the maximum sum of a subarray of size k
#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

// Function to find the maximum sum of a subarray of size k
int maxSubarraySum(const vector<int>& arr, int k) {
    // Check for invalid input
    if (arr.empty() || k <= 0 || k > arr.size()) {
        return -1; // Indicate an error or invalid input
    }

    int maxSum = 0; // Initialize the maximum sum
    int currentSum = 0; // Initialize the current sum for the window

    // Calculate the initial sum of the first k elements
    for (int i = 0; i < k; ++i) {
        currentSum += arr[i];
    }
    maxSum = currentSum;

    // Slide the window through the array
    for (int i = k; i < arr.size(); ++i) {
        // Subtract the first element of the previous window and add the next element
        currentSum += arr[i] - arr[i - k];

        // Update the maximum sum if the current sum is greater
        maxSum = max(maxSum, currentSum);
    }

    return maxSum;
}

int main() {
    vector<int> arr = {1, 4, 2, 10, 2, 3, 1, 0, 20};
    int k = 4; // Size of the subarray

    int maxSum = maxSubarraySum(arr, k);

    if (maxSum != -1) {
        cout << "Maximum sum of a subarray of size " << k << " is: " << maxSum << endl;
    } else {
        cout << "Invalid input or error occurred." << endl;
    }

    return 0;
}

