#include <iostream>
#include <vector>

// Assuming a minimum degree t for the B-Tree
class BTreeNode {
public:
    std::vector<int> keys; // Array to hold the keys
    int t;             // Minimum degree (defines range for number of keys)
    BTreeNode** children; // Array of child pointers
    int n;             // Current number of keys
    bool leaf;         // Is true when node is leaf. Otherwise false

    BTreeNode(int t, bool leaf);

    // Function to traverse all nodes in a subtree rooted with this node
    void traverse();

    // Function to search key k in subtree rooted with this node
    BTreeNode* search(int k);   // returns NULL if k is not present.

    // A function to split the child y of this node
    // Note that y must be full when this function is called
    void splitChild(int i, BTreeNode* y);

    // A function to insert a new key in this B-Tree node
    void insertNonFull(int k);

    // A function to delete a key from this node.
    void deleteKey(int k);

    // A function to find the predecessor of a node
    int findPredecessor();

    // A function to find the successor of a node
    int findSuccessor();

    // A function to remove a key from a leaf node
    void removeFromLeaf(int idx);

    // A function to remove a key from a non-leaf node
    void removeFromNonLeaf(int idx);

    // A function to fill the child node (at index idx) if it has less than t keys
    void fill(int idx);

    // A function to borrow a key from the previous child
    void borrowFromPrev(int idx);

    // A function to borrow a key from the next child
    void borrowFromNext(int idx);

    // A function to merge the child node (at index idx) with its sibling
    void merge(int idx);
};

class BTree {
public:
    BTreeNode* root; // Pointer to root node
    int t;      // Minimum degree

    BTree(int t);

    // function to traverse the tree
    void traverse();

    // function to search a key in the tree
    BTreeNode* search(int k); // returns NULL if k is not present.

    // The main function that inserts a new key in this B-Tree
    void insert(int k);

    // The main function that deletes a new key in this B-Tree
    void remove(int k);
};

// Constructor for BTreeNode class
BTreeNode::BTreeNode(int t1, bool leaf1) {
    t = t1;
    leaf = leaf1;

    keys.resize(2 * t - 1); // Allocate memory for maximum keys
    children = new BTreeNode* [2 * t]; // Allocate memory for maximum children

    n = 0; // Initialize number of keys as 0
}

// Function to traverse all nodes in a subtree rooted with this node
void BTreeNode::traverse() {
    // There are n keys and n+1 children, travers through n keys
    int i;
    for (i = 0; i < n; i++) {
        // If this is not leaf, then before printing key[i],
        // traverse the subtree rooted with child children[i].
        if (leaf == false)
            children[i]->traverse();
        std::cout << " " << keys[i];
    }

    // After printing the last key, traverse the subtree rooted
    // with last child
    if (leaf == false)
        children[i]->traverse();
}

// Function to search key k in subtree rooted with this node
BTreeNode* BTreeNode::search(int k) {
    // Find the first key greater than or equal to k
    int i = 0;
    while (i < n && k > keys[i])
        i++;

    // If the key is found at the current node
    if (keys[i] == k)
        return this;

    // If the key is not found here and this is a leaf node
    if (leaf == true)
        return nullptr;

    // Go to the appropriate child
    return children[i]->search(k);
}

// The main function that inserts a new key in this B-Tree
void BTree::insert(int k) {
    // If tree is empty
    if (root == nullptr) {
        // Allocate memory for root
        root = new BTreeNode(t, true);
        root->keys[0] = k;  // Insert key
        root->n = 1;  // Update number of keys in root
    } else {
        // If root is full, then tree grows in height
        if (root->n == 2 * t - 1) {
            // Allocate memory for new root
            BTreeNode* s = new BTreeNode(t, false);

            // Make old root as child of new root
            s->children[0] = root;

            // Split the old root and move 1 key to the new root
            s->splitChild(0, root);

            // New root has two children now.  Decide which of the
            // two children is going to have new key
            int i = 0;
            if (s->keys[0] < k)
                i++;
            s->children[i]->insertNonFull(k);

            // Change root
            root = s;
        } else  // If root is not full, call insertNonFull for root
            root->insertNonFull(k);
    }
}

// A function to insert a new key in this B-Tree node
void BTreeNode::insertNonFull(int k) {
    // Initialize index as index of rightmost element
    int i = n - 1;

    // If this is a leaf node
    if (leaf == true) {
        // The following loop does two things
        // a) Finds the location of new key to be inserted
        // b) Moves all greater keys to one place ahead
        while (i >= 0 && keys[i] > k) {
            keys[i + 1] = keys[i];
            i--;
        }

        // Insert the new key at found location
        keys[i + 1] = k;
        n = n + 1;
    } else  // If this node is not leaf
    {
        // Find the child which is going to have the new key
        while (i >= 0 && keys[i] > k)
            i--;

        // See if the found child is full
        if (children[i + 1]->n == 2 * t - 1) {
            // If the child is full, then split it
            splitChild(i + 1, children[i + 1]);

            // After split, the middle key of children[i] moves up and
            // children[i] is splitted into two.  See which of the two
            // is going to have the new key
            if (keys[i + 1] < k)
                i++;
        }
        children[i + 1]->insertNonFull(k);
    }
}

// A function to split the child y of this node
// Note that y must be full when this function is called
void BTreeNode::splitChild(int i, BTreeNode* y) {
    // Create a new node which is going to store t-1 keys
    // of y
    BTreeNode* z = new BTreeNode(y->t, y->leaf);
    z->n = t - 1;

    // Copy the last t-1 keys of y to z
    for (int j = 0; j < t - 1; j++)
        z->keys[j] = y->keys[j + t];

    // Copy the last t children of y to z
    if (y->leaf == false) {
        for (int j = 0; j < t; j++)
            z->children[j] = y->children[j + t];
    }

    // Reduce the number of keys in y
    y->n = t - 1;

    // Since this node is going to have a new child, create space of new
    // child
    for (int j = n; j >= i + 1; j--)
        children[j + 1] = children[j];

    // Link the new child to this node
    children[i + 1] = z;

    // A key of y will move to this node. Find the location of
    // new key and move all greater keys one space ahead
    for (int j = n - 1; j >= i; j--)
        keys[j + 1] = keys[j];

    // Copy the middle key of y to this node
    keys[i] = y->keys[t - 1];

    // Increment number of keys in this node
    n = n + 1;
}

// Constructor for BTree class
BTree::BTree(int t1) {
    root = nullptr;
    t = t1;
}

// Function to traverse the B-Tree
void BTree::traverse() {
    if (root != nullptr)
        root->traverse();
}

// Function to search the B-Tree
BTreeNode* BTree::search(int k) {
    return (root == nullptr) ? nullptr : root->search(k);
}

// Function to remove the key from B-Tree
void BTree::remove(int k) {
    if (!root) {
        std::cout << "The tree is empty\n";
        return;
    }

    root->deleteKey(k);

    if (root->n == 0) {
        BTreeNode* tmp = root;
        if (root->leaf) {
            root = nullptr;
        } else {
            root = root->children[0];
        }

        delete tmp;
    }
}

// A function to delete a key from this node.
void BTreeNode::deleteKey(int k) {
    int idx = 0;
    while (idx < n && keys[idx] < k) {
        ++idx;
    }

    if (idx < n && keys[idx] == k) {
        // The key to be removed is present in this node
        if (leaf) {
            removeFromLeaf(idx);
        } else {
            removeFromNonLeaf(idx);
        }
    } else {
        // If the key is not present in this node, it is present in the
        // subtree rooted with one of the children, we have to check if the child is full
        if (leaf) {
            std::cout << "The key " << k << " is does not exist in the tree\n";
            return;
        }

        // If the key is not present in this node, then the key will be present
        // in the subtree rooted with the child children[idx]
        bool flag = (idx == n);

        // If the child[idx] has less than t keys, we fill that child
        if (children[idx]->n < t) {
            fill(idx);
        }

        // If the last child has been merged, then we must have
        // merged it with the previous child and so we recurse on the (idx-1)th child. Else, we recurse on the (idx)th child.
        if (flag && idx > n) {
            children[idx - 1]->deleteKey(k);
        } else {
            children[idx]->deleteKey(k);
        }
    }
}

// A function to remove the key from a leaf node
void BTreeNode::removeFromLeaf(int idx) {
    // Move all the keys after the idx-th key one step back
    for (int i = idx + 1; i < n; ++i) {
        keys[i - 1] = keys[i];
    }

    // Reduce the count of keys
    n--;
}

// A function to remove the key from a non-leaf node
void BTreeNode::removeFromNonLeaf(int idx) {
    int k = keys[idx];

    // If the child that precedes k (children[idx]) has at least t keys, find the predecessor 'pred' of k in the subtree rooted at
    // children[idx]. Replace k by pred. Recursively delete pred in children[idx]
    if (children[idx]->n >= t) {
        int pred = findPredecessor();
        keys[idx] = pred;
        children[idx]->deleteKey(pred);
    } else if (children[idx + 1]->n >= t) {
        // If the child children[idx+1] that succeeds k has at least t keys, find the successor 'succ' of k in the subtree rooted at
        // children[idx+1]. Replace k by succ. Recursively delete succ in children[idx+1]
        int succ = findSuccessor();
        keys[idx] = succ;
        children[idx + 1]->deleteKey(succ);
    } else {
        // If both children[idx] and children[idx+1] has less than t keys,merge k and all of children[idx+1] into children[idx]
        // Now children[idx] contains 2t-1 keys
        // Free children[idx+1] and recursively delete k from children[idx]
        merge(idx);
        children[idx]->deleteKey(k);
    }
}

// A function to find predecessor of k (or the key at index idx in keys[])
int BTreeNode::findPredecessor() {
    // Keep moving to the rightmost node until we reach a leaf
    BTreeNode* cur = children[0];
    while (!cur->leaf) {
        cur = cur->children[cur->n];
    }

    // Return the last key of the leaf
    return cur->keys[cur->n - 1];
}

// A function to find successor of k (or the key at index idx in keys[])
int BTreeNode::findSuccessor() {
    // Keep moving the leftmost node starting from children[idx+1] until we reach a leaf
    BTreeNode* cur = children[1];
    while (!cur->leaf) {
        cur = cur->children[0];
    }

    // Return the first key of the leaf
    return cur->keys[0];
}

// A function to fill child children[idx] which has less than t-1 keys
void BTreeNode::fill(int idx) {
    // If the previous child(children[idx-1]) has more than t-1 keys, borrow a key from that child
    if (idx != 0 && children[idx - 1]->n >= t) {
        borrowFromPrev(idx);
    } else if (idx != n && children[idx + 1]->n >= t) {
        // If the next child(children[idx+1]) has more than t-1 keys, borrow a key from that child
        borrowFromNext(idx);
    } else {
        // Merge children[idx] with its sibling
        // If children[idx] is the last child, merge it with with its previous sibling
        // Otherwise merge it with its next sibling
        if (idx != n) {
            merge(idx);
        } else {
            merge(idx - 1);
        }
    }
}

// A function to borrow a key from children[idx-1] and insert it into children[idx]
void BTreeNode::borrowFromPrev(int idx) {
    BTreeNode* child = children[idx];
    BTreeNode* sibling = children[idx - 1];

    // The last key from children[idx-1] goes to the parent and children[idx-1]'s rightmost child moves to children[idx]'s leftmost place
    // Keys[idx-1] is inserted as the first key in children[idx].
    // children[idx-1]'s last child is inserted as children[idx]'s first child

    // Moving all key in children[idx] one step ahead
    for (int i = child->n - 1; i >= 0; --i) {
        child->keys[i + 1] = child->keys[i];
    }

    // If children[idx] is not a leaf, move all its child pointers one step ahead
    if (!child->leaf) {
        for (int i = child->n; i >= 0; --i) {
            child->children[i + 1] = child->children[i];
        }
    }

    // Setting children[idx]'s first key equal to keys[idx-1] from the current node
    child->keys[0] = keys[idx - 1];

    // Moving sibling's last child as children[idx]'s first child
    if (!child->leaf) {
        child->children[0] = sibling->children[sibling->n];
    }

    // Moving the key from the sibling to the parent
    // This key will be inserted at keys[idx-1]
    keys[idx - 1] = sibling->keys[sibling->n - 1];

    // Increase children[idx] key count and decrease sibling's key count
    child->n += 1;
    sibling->n -= 1;
}

// A function to borrow a key from the children[idx+1] and insert it into children[idx]
void BTreeNode::borrowFromNext(int idx) {
    BTreeNode* child = children[idx];
    BTreeNode* sibling = children[idx + 1];

    // keys[idx] is inserted as the last key in children[idx]
    child->keys[(child->n)] = keys[idx];

    // Sibling's first child is inserted as children[idx]'s last child
    if (!child->leaf) {
        child->children[(child->n) + 1] = sibling->children[0];
    }

    //The first key from sibling is inserted in keys[idx]
    keys[idx] = sibling->keys[0];

    // Moving all keys in sibling one step behind
    for (int i = 1; i < sibling->n; ++i) {
        sibling->keys[i - 1] = sibling->keys[i];
    }

    // Moving the child pointers one step behind
    if (!sibling->leaf) {
        for (int i = 1; i <= sibling->n; ++i) {
            sibling->children[i - 1] = sibling->children[i];
        }
    }

    // Increasing and decreasing the key count of children[idx] and children[idx+1]
    child->n += 1;
    sibling->n -= 1;
}

// A function to merge children[idx] with children[idx+1]
// children[idx+1] is freed after merging
void BTreeNode::merge(int idx) {
    BTreeNode* child = children[idx];
    BTreeNode* sibling = children[idx + 1];

    // Pulling a key from the current node and inserting it into (t-1)th
    // position of children[idx]
    child->keys[t - 1] = keys[idx];

    // Copying all keys from children[idx+1] to children[idx]
    for (int i = 0; i < sibling->n; ++i) {
        child->keys[i + t] = sibling->keys[i];
    }

    // Copying the child pointers from children[idx+1] to children[idx]
    if (!child->leaf) {
        for (int i = 0; i <= sibling->n; ++i) {
            child->children[i + t] = sibling->children[i];
        }
    }

    // Moving all keys after idx in the current node one step before - to fill the gap created by moving keys[idx] to children[idx]
    for (int i = idx + 1; i < n; ++i) {
        keys[i - 1] = keys[i];
    }

    // Moving the child pointers after (idx+1) in the current node one step before
    for (int i = idx + 2; i <= n; ++i) {
        children[i - 1] = children[i];
    }

    // Updating the key count of child
    child->n += sibling->n + 1;

    // Reducing the key count of current node
    n--;

    // Freeing the memory occupied by sibling
    delete sibling;
}

int main() {
    BTree t(3); // A B-Tree with minium degree 3
    t.insert(10);
    t.insert(20);
    t.insert(5);
    t.insert(6);
    t.insert(12);
    t.insert(30);
    t.insert(7);
    t.insert(17);

    std::cout << "Traversal of the constucted tree is ";
    t.traverse();
    std::cout << std::endl;

    int k = 6;
    (t.search(k) != nullptr) ? std::cout << "\nPresent " : std::cout << "\nNot Present ";

    k = 15;
    (t.search(k) != nullptr) ? std::cout << "\nPresent " : std::cout << "\nNot Present ";

    std::cout << "\nDeleting 12\n";
    t.remove(12);
    std::cout << "Traversal of the tree after deleting 12\n";
    t.traverse();
    std::cout << std::endl;

    std::cout << "\nDeleting 6\n";
    t.remove(6);
    std::cout << "Traversal of the tree after deleting 6\n";
    t.traverse();
    std::cout << std::endl;

    return 0;
}

