// C++ implementation of Trie data structure
#include <iostream>
#include <string>
#include <vector>

using namespace std;

const int ALPHABET_SIZE = 26; // Assuming lowercase English alphabet

// Represents a node in the Trie
struct TrieNode {
    TrieNode* children[ALPHABET_SIZE];
    bool isEndOfWord;

    TrieNode() {
        isEndOfWord = false;
        for (int i = 0; i < ALPHABET_SIZE; i++)
            children[i] = nullptr;
    }
};

class Trie {
private:
    TrieNode* root;

public:
    Trie() {
        root = new TrieNode();
    }

    // Inserts a word into the Trie
    void insert(string key) {
        TrieNode* curr = root;
        for (char c : key) {
            int index = c - 'a'; // Calculate the index for the character
            if (curr->children[index] == nullptr) {
                curr->children[index] = new TrieNode();
            }
            curr = curr->children[index];
        }
        curr->isEndOfWord = true; // Mark the last node as the end of the word
    }

    // Searches for a word in the Trie
    bool search(string key) {
        TrieNode* curr = root;
        for (char c : key) {
            int index = c - 'a';
            if (curr->children[index] == nullptr) {
                return false; // Key not found
            }
            curr = curr->children[index];
        }
        return (curr != nullptr && curr->isEndOfWord); // Return true if the key exists and is the end of a word
    }

    // Checks if there is any word in the trie that starts with the given prefix
    bool startsWith(string prefix) {
        TrieNode* curr = root;
        for (char c : prefix) {
            int index = c - 'a';
            if (curr->children[index] == nullptr) {
                return false; // Prefix not found
            }
            curr = curr->children[index];
        }
        return true; // Prefix found (it may or may not be a complete word)
    }
};

int main() {
    Trie trie;

    // Inserting words into the trie
    trie.insert("apple");
    trie.insert("app");
    trie.insert("application");

    // Searching for words
    cout << "Search \"apple\": " << trie.search("apple") << endl;      // Output: 1 (true)
    cout << "Search \"app\": " << trie.search("app") << endl;          // Output: 1 (true)
    cout << "Search \"appl\": " << trie.search("appl") << endl;        // Output: 0 (false)
    cout << "Search \"application\": " << trie.search("application") << endl; // Output: 1 (true)
    cout << "Search \"banana\": " << trie.search("banana") << endl;     // Output: 0 (false)

    // Checking for prefixes
    cout << "Starts with \"app\": " << trie.startsWith("app") << endl;      // Output: 1 (true)
    cout << "Starts with \"appl\": " << trie.startsWith("appl") << endl;     // Output: 1 (true)
    cout << "Starts with \"bana\": " << trie.startsWith("bana") << endl;    // Output: 0 (false)

    return 0;
}

