#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

// Segment Tree implementation for Range Sum Queries

class SegmentTree {
private:
    vector<int> tree; // The segment tree array
    vector<int> arr;  // The original array
    int n;           // The size of the original array

    // Build the segment tree recursively
    void buildTree(int node, int start, int end) {
        if (start == end) {
            tree[node] = arr[start]; // Leaf node stores the value from the original array
        } else {
            int mid = (start + end) / 2;
            buildTree(2 * node + 1, start, mid); // Build left subtree
            buildTree(2 * node + 2, mid + 1, end); // Build right subtree
            tree[node] = tree[2 * node + 1] + tree[2 * node + 2]; // Internal node stores the sum of its children
        }
    }

    // Perform a range sum query recursively
    int queryRange(int node, int start, int end, int left, int right) {
        if (right < start || end < left) {
            return 0; // No overlap
        } else if (left <= start && end <= right) {
            return tree[node]; // Complete overlap
        } else {
            int mid = (start + end) / 2;
            int p1 = queryRange(2 * node + 1, start, mid, left, right); // Query left subtree
            int p2 = queryRange(2 * node + 2, mid + 1, end, left, right); // Query right subtree
            return p1 + p2; // Sum of the results from the subtrees
        }
    }

    // Update a value in the array and update the segment tree accordingly
    void updateValue(int node, int start, int end, int idx, int val) {
        if (start == end) {
            arr[idx] = val; // Update the original array
            tree[node] = val; // Update the leaf node
        } else {
            int mid = (start + end) / 2;
            if (idx >= start && idx <= mid) {
                updateValue(2 * node + 1, start, mid, idx, val); // Update left subtree
            } else {
                updateValue(2 * node + 2, mid + 1, end, idx, val); // Update right subtree
            }
            tree[node] = tree[2 * node + 1] + tree[2 * node + 2]; // Update the internal node
        }
    }

public:
    // Constructor: Initializes the segment tree with the given array
    SegmentTree(const vector<int>& input_arr) : arr(input_arr), n(input_arr.size()) {
        // The size of the segment tree is approximately 4*n
        tree.resize(4 * n); 
        buildTree(0, 0, n - 1); // Build the segment tree from the root node
    }

    // Public method to perform a range sum query
    int query(int left, int right) {
        return queryRange(0, 0, n - 1, left, right); // Start the query from the root node
    }

    // Public method to update a value in the array and the segment tree
    void update(int idx, int val) {
        updateValue(0, 0, n - 1, idx, val); // Start the update from the root node
    }
};

int main() {
    vector<int> arr = {1, 3, 5, 7, 9, 11};
    SegmentTree st(arr);

    // Example usage:
    cout << "Sum of range [1, 3]: " << st.query(1, 3) << endl; // Expected: 3 + 5 + 7 = 15

    st.update(1, 10); // Update arr[1] to 10

    cout << "Sum of range [1, 3] after update: " << st.query(1, 3) << endl; // Expected: 10 + 5 + 7 = 22

    return 0;
}

