#include <iostream>
#include <vector>

using namespace std;

class FenwickTree {
private:
  vector<int> bit; // The Binary Indexed Tree array
  int n;           // The size of the input array

public:
  // Constructor: Initializes the Fenwick Tree with the given array size
  FenwickTree(int n) : n(n), bit(n + 1, 0) {}

  // Constructor: Builds the Fenwick Tree from the given input array
  FenwickTree(const vector<int>& arr) : n(arr.size()), bit(n + 1, 0) {
    for (int i = 0; i < n; ++i) {
      update(i, arr[i]); // Build the tree by adding each element
    }
  }

  // Function to update the value at a given index by adding 'val'
  void update(int idx, int val) {
    idx++; // Fenwick Tree indices are 1-based
    while (idx <= n) {
      bit[idx] += val;  // Add 'val' to the current node
      idx += idx & -idx; // Move to the parent node (next index to update)
    }
  }

  // Function to calculate the prefix sum up to the given index
  int query(int idx) {
    idx++; // Fenwick Tree indices are 1-based
    int sum = 0;
    while (idx > 0) {
      sum += bit[idx];  // Add the value of the current node
      idx -= idx & -idx; // Move to the child node (next index to query)
    }
    return sum;
  }

  // Function to calculate the sum of elements in the range [left, right]
  int rangeSum(int left, int right) {
      return query(right) - (left > 0 ? query(left - 1) : 0);
  }
};

int main() {
  vector<int> arr = {2, 1, 1, 3, 2, 3, 4, 5, 6, 7, 8, 9};
  FenwickTree ft(arr); // Create a Fenwick Tree from the array

  cout << "Sum of elements from index 0 to 5: " << ft.query(5) << endl; // Output: 12 (2+1+1+3+2+3)
  cout << "Sum of elements from index 2 to 7: " << ft.rangeSum(2,7) << endl; // Output: 18
  ft.update(3, 6); // Update the value at index 3 by adding 6 (arr[3] becomes 9)
  cout << "Sum of elements from index 0 to 5 after update: " << ft.query(5) << endl; // Output: 18 (2+1+1+9+2+3)

  return 0;
}

