#include <iostream>
#include <vector>

class DisjointSetUnion {
private:
    std::vector<int> parent;
    std::vector<int> rank; // Used for union by rank optimization
    int num_sets;

public:
    // Constructor: Initializes the DSU with n elements, each in its own set.
    DisjointSetUnion(int n) : parent(n), rank(n, 0), num_sets(n) {
        for (int i = 0; i < n; ++i) {
            parent[i] = i; // Each element is initially its own parent
        }
    }

    // Find: Finds the representative (root) of the set that element x belongs to.
    // Implements path compression for optimization.
    int find(int x) {
        if (parent[x] != x) {
            parent[x] = find(parent[x]); // Path compression: directly connect x to the root
        }
        return parent[x];
    }

    // Union: Merges the sets containing elements x and y.
    // Implements union by rank for optimization.
    void unite(int x, int y) {
        int rootX = find(x);
        int rootY = find(y);

        if (rootX != rootY) {
            if (rank[rootX] < rank[rootY]) {
                parent[rootX] = rootY; // Attach shorter tree to the taller tree
            } else if (rank[rootX] > rank[rootY]) {
                parent[rootY] = rootX;
            } else {
                parent[rootY] = rootX; // If ranks are equal, choose one as the parent
                rank[rootX]++;         // Increment the rank of the new parent
            }
          num_sets--;
        }
    }

    // Function to determine how many disjoint sets exist
    int get_num_sets() { return num_sets; }
};

int main() {
    // Example usage:
    int n = 5; // Create a DSU with 5 elements
    DisjointSetUnion dsu(n);

    std::cout << "Initial number of sets: " << dsu.get_num_sets() << std::endl;

    dsu.unite(0, 1); // Merge sets containing 0 and 1
    std::cout << "Number of sets after uniting 0 and 1: " << dsu.get_num_sets() << std::endl;
    dsu.unite(2, 3); // Merge sets containing 2 and 3
    std::cout << "Number of sets after uniting 2 and 3: " << dsu.get_num_sets() << std::endl;
    dsu.unite(1, 2); // Merge sets containing 1 and 2
    std::cout << "Number of sets after uniting 1 and 2: " << dsu.get_num_sets() << std::endl;

    // Check if 0 and 3 are in the same set
    if (dsu.find(0) == dsu.find(3)) {
        std::cout << "0 and 3 are in the same set.\n";
    } else {
        std::cout << "0 and 3 are in different sets.\n";
    }

    return 0;
}

