#include <iostream>
#include <vector>
#include <algorithm>

// Max Heap implementation
class MaxHeap {
private:
    std::vector<int> heap;

    // Heapify a subtree rooted with node i which is
    // an index in heap[]. n is size of heap
    void heapify(int i) {
        int n = heap.size();
        int largest = i; // Initialize largest as root
        int l = 2 * i + 1; // left = 2*i + 1
        int r = 2 * i + 2; // right = 2*i + 2

        // If left child is larger than root
        if (l < n && heap[l] > heap[largest])
            largest = l;

        // If right child is larger than largest so far
        if (r < n && heap[r] > heap[largest])
            largest = r;

        // If largest is not root
        if (largest != i) {
            std::swap(heap[i], heap[largest]);

            // Recursively heapify the affected sub-tree
            heapify(largest);
        }
    }

public:
    // Insert a new key
    void insert(int key) {
        heap.push_back(key);
        int i = heap.size() - 1;

        // Fix the max heap property if it is violated
        while (i != 0 && heap[(i - 1) / 2] < heap[i]) {
            std::swap(heap[i], heap[(i - 1) / 2]);
            i = (i - 1) / 2;
        }
    }

    // Extracts the maximum value
    int extractMax() {
        if (heap.empty()) {
            return -1; // Or throw an exception
        }
        if (heap.size() == 1) {
            int root = heap[0];
            heap.pop_back();
            return root;
        }

        // Store the maximum value, and remove it from heap
        int root = heap[0];
        heap[0] = heap.back();
        heap.pop_back();
        heapify(0);

        return root;
    }

    // Returns the maximum value without removing it
    int getMax() const {
        if (!heap.empty()) {
            return heap[0];
        } else {
            return -1; // Or throw an exception
        }
    }

    // Checks if the heap is empty
    bool isEmpty() const {
        return heap.empty();
    }

    // Prints the heap
    void printHeap() const {
        for (int value : heap) {
            std::cout << value << " ";
        }
        std::cout << std::endl;
    }
};

int main() {
    MaxHeap maxHeap;
    maxHeap.insert(5);
    maxHeap.insert(3);
    maxHeap.insert(17);
    maxHeap.insert(10);
    maxHeap.insert(84);
    maxHeap.insert(19);
    maxHeap.insert(6);
    maxHeap.insert(22);
    maxHeap.insert(9);

    std::cout << "Max Heap elements: ";
    maxHeap.printHeap();

    std::cout << "Max element: " << maxHeap.getMax() << std::endl;

    std::cout << "Extract Max: " << maxHeap.extractMax() << std::endl;
    std::cout << "Heap after extraction: ";
    maxHeap.printHeap();

    return 0;
}

