 #include <iostream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

// Structure to represent a suffix and its starting index
struct Suffix {
    int index;
    string suffix;
};

// Custom comparison function to sort suffixes lexicographically
bool compareSuffixes(const Suffix& a, const Suffix& b) {
    return a.suffix < b.suffix;
}

// Function to build the suffix array
vector<int> buildSuffixArray(const string& text) {
    int n = text.length();
    vector<Suffix> suffixes(n);

    // Populate the suffixes vector
    for (int i = 0; i < n; ++i) {
        suffixes[i].index = i;
        suffixes[i].suffix = text.substr(i);
    }

    // Sort the suffixes using the custom comparison function
    sort(suffixes.begin(), suffixes.end(), compareSuffixes);

    // Create the suffix array by extracting the indices
    vector<int> suffixArray(n);
    for (int i = 0; i < n; ++i) {
        suffixArray[i] = suffixes[i].index;
    }

    return suffixArray;
}

// Function to search for a pattern in the text using the suffix array
int searchPattern(const string& text, const vector<int>& suffixArray, const string& pattern) {
    int n = text.length();
    int m = pattern.length();
    int low = 0, high = n - 1;

    while (low <= high) {
        int mid = low + (high - low) / 2;
        int suffixIndex = suffixArray[mid];
        string suffix = text.substr(suffixIndex);

        // Compare the pattern with the current suffix
        int result = suffix.compare(0, m, pattern);

        if (result == 0) {
            // Pattern found at the current suffix index
            return suffixIndex;
        } else if (result < 0) {
            // Pattern is lexicographically greater, search in the right half
            low = mid + 1;
        } else {
            // Pattern is lexicographically smaller, search in the left half
            high = mid - 1;
        }
    }

    // Pattern not found
    return -1;
}

int main() {
    string text = "banana";
    string pattern = "ana";

    // Build the suffix array
    vector<int> suffixArray = buildSuffixArray(text);

    cout << "Suffix Array: ";
    for (int index : suffixArray) {
        cout << index << " ";
    }
    cout << endl;

    // Search for the pattern in the text
    int index = searchPattern(text, suffixArray, pattern);

    if (index != -1) {
        cout << "Pattern found at index: " << index << endl;
    } else {
        cout << "Pattern not found." << endl;
    }

    return 0;
}

