#include <iostream>
#include <vector>
#include <algorithm>

// A B-Tree node
class BTreeNode {
public:
    std::vector<int> keys;   // Vector to store keys
    std::vector<BTreeNode*> children; // Vector to store child pointers
    int t;                 // Minimum degree (defines the range for number of keys)
    bool leaf;              // True when node is leaf. Otherwise false

    BTreeNode(int t, bool leaf);

    // Function to find the first key greater than or equal to k
    int findKey(int k);

    // Function to remove key k from the sub-tree rooted with this node
    void remove(int k);

    // A function to remove the key present in this node
    void removeFromLeaf(int idx);

    // A function to remove the key present in this node
    // that is NOT a leaf node
    void removeFromNonLeaf(int idx);

    // Function to get predecessor of keys[idx]
    int getPred(int idx);

    // Function to get successor of keys[idx]
    int getSucc(int idx);

    // Function to fill child c[idx] if it has less than t keys
    void fill(int idx);

    // Function to borrow a key from c[idx-1] and place it in c[idx]
    void borrowFromPrev(int idx);

    // Function to borrow a key from c[idx+1] and place it in c[idx]
    void borrowFromNext(int idx);

    // Function to merge c[idx] with c[idx+1]
    void merge(int idx);

    // Function to insert a new key in this node
    void insertNonFull(int k);

    // Function to split the child of this node
    void splitChild(int i, BTreeNode *y);

    void traverse();

    ~BTreeNode();
};

// A B-Tree
class BTree {
public:
    BTreeNode *root; // Pointer to root node
    int t;      // Minimum degree

    BTree(int t);

    // function to traverse the tree
    void traverse() { if (root != nullptr) root->traverse(); }

    // Function to search key k in tree
    BTreeNode* search(int k);

    // Function to insert key k in tree
    void insert(int k);

    void remove(int k);

    ~BTree();
};

// Constructor for BTreeNode class
BTreeNode::BTreeNode(int t1, bool leaf1) {
    t = t1;
    leaf = leaf1;
}

// Function to find the first key greater than or equal to k
int BTreeNode::findKey(int k) {
    int idx = 0;
    while (idx < keys.size() && keys[idx] < k)
        ++idx;
    return idx;
}

// Function to remove key k from the sub-tree rooted with this node
void BTreeNode::remove(int k) {
    int idx = findKey(k);

    // The key to be removed is present in this node
    if (idx < keys.size() && keys[idx] == k {
        if (leaf)
            removeFromLeaf(idx);
        else
            removeFromNonLeaf(idx);
    } else {
        // If this node is a leaf node, then the key is not present in the tree
        if (leaf) {
            std::cout << "The key " << k << " is does not exist in the tree\n";
            return;
        }

        // The key to be removed is present in the sub-tree rooted with this node
        bool flag = (idx == keys.size());

        // If the child c[idx] has less than t keys, we fill it before
        // recursively calling remove() on c[idx]
        if (children[idx]->keys.size() < t)
            fill(idx);

        // If the key lies in c[idx] after filling, then recursive remove
        // Function is called on it
        if (flag && idx > keys.size())
            children[idx - 1]->remove(k);
        else
            children[idx]->remove(k);
    }
}

// Function to remove the key present in this node
// that is a leaf node
void BTreeNode::removeFromLeaf(int idx) {
    // Move all the keys after the idx-th key one step back
    for (int i = idx + 1; i < keys.size(); ++i)
        keys[i - 1] = keys[i];

    // Reduce the number of keys
    keys.pop_back();
}

// Function to remove the key present in this node
// that is NOT a leaf node
void BTreeNode::removeFromNonLeaf(int idx) {
    int k = keys[idx];

    // If the child c[idx] has more than t-1 keys, find the predecessor
    // 'pred' of k in the subtree rooted at c[idx]. Replace k by pred.
    // Recursively delete pred in c[idx]
    if (children[idx]->keys.size() >= t) {
        int pred = getPred(idx);
        keys[idx] = pred;
        children[idx]->remove(pred);
    }

    // If the child c[idx+1] has more than t-1 keys, find the successor
    // 'succ' of k in the subtree rooted at c[idx+1]
    // Replace k by succ
    // Recursively delete succ in c[idx+1]
    else if (children[idx + 1]->keys.size() >= t) {
        int succ = getSucc(idx);
        keys[idx] = succ;
        children[idx + 1]->remove(succ);
    }

    // If both c[idx] and c[idx+1] has less than t-1 keys,merge k and all of c[idx+1]
    // into c[idx]
    // Now c[idx] contains 2t-1 keys
    // Free c[idx+1] and recursively delete k from c[idx]
    else {
        merge(idx);
        children[idx]->remove(k);
    }
}

// Function to get predecessor of keys[idx]
int BTreeNode::getPred(int idx) {
    // Keep moving to the right most node until we reach a leaf
    BTreeNode *cur = children[idx];
    while (!cur->leaf)
        cur = cur->children[cur->keys.size()];

    // Return the last key of the leaf
    return cur->keys[cur->keys.size() - 1];
}

int BTreeNode::getSucc(int idx) {
    // Keep moving the left most node starting from c[idx+1] until we reach a leaf
    BTreeNode *cur = children[idx + 1];
    while (!cur->leaf)
        cur = cur->children[0];

    // Return the first key of the leaf
    return cur->keys[0];
}

// Function to fill child c[idx] if it has less than t keys
void BTreeNode::fill(int idx) {
    // If the previous child(c[idx-1]) has more than t-1 keys, borrow a key
    // from that child
    if (idx != 0 && children[idx - 1]->keys.size() >= t)
        borrowFromPrev(idx);

    // If the next child(c[idx+1]) has more than t-1 keys, borrow a key
    // from that child
    else if (idx != keys.size() && children[idx + 1]->keys.size() >= t)
        borrowFromNext(idx);

    // Merge c[idx] with its sibling
    // If c[idx] is the last child, merge it with with its previous sibling
    // Otherwise merge it with its next sibling
    else {
        if (idx != keys.size())
            merge(idx);
        else
            merge(idx - 1);
    }
}

// Function to borrow a key from c[idx-1] and place it in c[idx]
void BTreeNode::borrowFromPrev(int idx) {
    BTreeNode *child = children[idx];
    BTreeNode *sibling = children[idx - 1];

    // The last key from C[idx-1] goes to the parent and key[idx-1]
    // from parent is inserted as the first key in C[idx]. Thus, the sibling loses
    // one key and child gains one key

    // Moving all key in c[idx] one step ahead
    child->keys.insert(child->keys.begin(), keys[idx - 1]);

    // If c[idx-1] is not a leaf, move last child from C[idx-1] to C[idx]
    if (!child->leaf)
        child->children.insert(child->children.begin(), sibling->children[sibling->keys.size()]);

    // Moving the key from the sibling to the parent
    // This reduces the number of keys in the sibling
    keys[idx - 1] = sibling->keys[sibling->keys.size() - 1];

    // Increase c[idx]'s key count
    // Reduce sibling's key count
    sibling->keys.pop_back();
    if(!sibling->leaf) sibling->children.pop_back();
}

// Function to borrow a key from c[idx+1] and place it in c[idx]
void BTreeNode::borrowFromNext(int idx) {
    BTreeNode *child = children[idx];
    BTreeNode *sibling = children[idx + 1];

    // keys[idx] is inserted as the last key in C[idx]
    child->keys.push_back(keys[idx]);

    // If C[idx+1] is not a leaf, move the first child from C[idx+1] to C[idx]
    if (!child->leaf)
        child->children.push_back(sibling->children[0]);

    // Moving the key from sibling to parent
    // This reduces the number of keys in the sibling
    keys[idx] = sibling->keys[0];

    // Increase c[idx]'s key count
    // Reduce sibling's key count
    sibling->keys.erase(sibling->keys.begin());
    if(!sibling->leaf) sibling->children.erase(sibling->children.begin());
}

// Function to merge c[idx] with c[idx+1]
void BTreeNode::merge(int idx) {
    BTreeNode *child = children[idx];
    BTreeNode *sibling = children[idx + 1];

    // Pulling a key from the current node and inserting it into (t-1) position of c[idx]
    child->keys.push_back(keys[idx]);

    // Copying all keys from c[idx+1] to c[idx]
    for (int i = 0; i < sibling->keys.size(); ++i)
        child->keys.push_back(sibling->keys[i]);

    // Copying the child pointers from c[idx+1] to c[idx]
    if (!child->leaf) {
        for (int i = 0; i < sibling->children.size(); ++i)
            child->children.push_back(sibling->children[i]);
    }

    // Moving all keys after idx in the current node one step before - done using erase
    keys.erase(keys.begin() + idx);
    children.erase(children.begin() + idx + 1);

    // Freeing the memory occupied by sibling
    delete sibling;
}

// Function to insert a new key in this node
void BTreeNode::insertNonFull(int k) {
    // Initialize index as index of rightmost element
    int i = keys.size() - 1;

    // If this is a leaf node
    if (leaf) {
        // The following loop does two things
        // a) Finds the location of new key to be inserted
        // b) Moves all greater keys to one place ahead
        while (i >= 0 && keys[i] > k) {
            i--;
        }

        keys.insert(keys.begin() + i + 1, k);
    } else {
        // Find the child which is going to have the new key
        while (i >= 0 && keys[i] > k)
            i--;

        // See if the found child is full
        if (children[i + 1]->keys.size() == 2 * t - 1) {
            // If the child is full, then split it
            splitChild(i + 1, children[i + 1]);

            // After split, the middle key of C[i] goes up and C[i] is splitted into two.
            // See which of the two is going to have the new key
            if (keys[i + 1] < k)
                i++;
        }
        children[i + 1]->insertNonFull(k);
    }
}

// Function to split the child of this node
void BTreeNode::splitChild(int i, BTreeNode *y) {
    // Create a new node which is going to store half of the keys of y
    BTreeNode *z = new BTreeNode(y->t, y->leaf);
    z->keys.resize(t - 1);

    // Copy the last t-1 keys of y to z
    for (int j = 0; j < t - 1; j++)
        z->keys[j] = y->keys[j + t];

    // Copy the last t children of y to z
    if (!y->leaf) {
        z->children.resize(t);
        for (int j = 0; j < t; j++)
            z->children[j] = y->children[j + t];
            y->children.resize(t); //remove the other half of children to ensure integrity.
    }

    // Reduce the number of keys in y
    y->keys.resize(t - 1);


    // Since this node is going to have a new child, create space of new child
    children.resize(keys.size() + 1);
    for(int j = keys.size(); j > i + 1; --j) children[j] = children[j-1];

    children[i + 1] = z;

    // A key of y will move to this node. Find the location of
    // new key and move all greater keys one space ahead
    keys.resize(keys.size() + 1);
    for(int j = keys.size() - 1; j > i; --j) keys[j] = keys[j-1];

    // Copy the middle key of y to this node
    keys[i] = y->keys[t - 1];

    // Finally, insert the new key into this node
    y->keys.pop_back(); // remove the last key after moving to parent
}

// Function to traverse all nodes in a subtree rooted with this node
void BTreeNode::traverse() {
    // There are keys.size() keys and keys.size() + 1 children, traverse through keys.size() keys
    // and first keys.size() children
    int i;
    for (i = 0; i < keys.size(); i++) {
        // If this is not leaf, then before printing key[i]
        // traverse the subtree rooted with child children[i].
        if (!leaf)
            children[i]->traverse();
        std::cout << " " << keys[i];
    }

    // Print the subtree rooted with last child
    if (!leaf)
        children[i]->traverse();
}

BTreeNode::~BTreeNode() {
    if (!leaf) {
        for (BTreeNode* child : children) {
            delete child;
        }
    }
}

// Constructor for BTree class
BTree::BTree(int t1) {
    root = nullptr;
    t = t1;
}

// Function to search key k in tree
BTreeNode* BTree::search(int k) {
    // Call the search function for root
    if(root) return search(root, k);
    else return nullptr;
}

BTreeNode* BTree::search(BTreeNode* node, int k) {
    int i = 0;
    while (i < node->keys.size() && k > node->keys[i])
        i++;

    if (i < node->keys.size() && node->keys[i] == k)
        return node;

    if (node->leaf)
        return nullptr;

    return search(node->children[i], k);
}

// Function to insert key k in tree
void BTree::insert(int k) {
    // If tree is empty
    if (root == nullptr) {
        // Allocate memory for root
        root = new BTreeNode(t, true);
        root->keys.push_back(k);  // Insert key
    } else {
        // If root is full, then tree grows in height
        if (root->keys.size() == 2 * t - 1) {
            // Allocate memory for new root
            BTreeNode *s = new BTreeNode(t, false);

            // Make old root as child of new root
            s->children.push_back(root);

            // Split the old root and move 1 key to the new root
            s->splitChild(0, root);

            // New root has two children now.  Decide which of the
            // two children is going to have new key
            int i = 0;
            if (s->keys[0] < k)
                i++;
            s->children[i]->insertNonFull(k);

            // Change root
            root = s;
        } else {
            // If root is not full, call insertNonFull for root
            root->insertNonFull(k);
        }
    }
}

void BTree::remove(int k) {
    if (!root) {
        std::cout << "The tree is empty\n";
        return;
    }

    root->remove(k);

    if (root->keys.empty()) {
        BTreeNode *tmp = root;
        if (!root->leaf)
            root = root->children[0];
        else
            root = nullptr;

        delete tmp;
    }
}

BTree::~BTree() {
    delete root;
}

// Driver program to test above functions
int main() {
    BTree t(3); // A B-Tree with minium degree 3 (2 < = t < = 3)
    t.insert(10);
    t.insert(20);
    t.insert(5);
    t.insert(6);
    t.insert(12);
    t.insert(30);
    t.insert(7);
    t.insert(17);

    std::cout << "Traversal of the constucted tree is ";
    t.traverse();
    std::cout << std::endl;

    int k = 6;
    (t.search(k) != nullptr) ? std::cout << std::endl << k << " is present"
                            : std::cout << std::endl << k << " is not present";

    k = 15;
    (t.search(k) != nullptr) ? std::cout << std::endl << k << " is present"
                            : std::cout << std::endl << k << " is not present";

