#include <iostream>
#include <vector>
#include <string>
#include <functional>
#include <random>

class BloomFilter {
private:
 std::vector<bool> bitArray; // The bit array
 size_t arraySize;        // Size of the bit array
 size_t numHashFunctions; // Number of hash functions
 std::vector<std::function<size_t(const std::string&)>> hashFunctions; // Vector to store hash functions

public:
 // Constructor
 BloomFilter(size_t size, size_t numHashes) : arraySize(size), numHashFunctions(numHashes), bitArray(size, false) {
 // Initialize hash functions (using different seeds for variety)
 std::random_device rd;
 std::mt19937 gen(rd());
 std::uniform_int_distribution<> distrib(1, size -1); // Ensure seed is within reasonable bounds

 for (size_t i = 0; i < numHashes; ++i) {
 size_t seed = distrib(gen); // Generate a different seed for each hash function.
 hashFunctions.push_back([seed, size](const std::string& str) {
 size_t hash = std::hash<std::string>{}(str + std::to_string(seed));
 return hash % size; // Ensure hash is within array bounds
 });
 }
 }

 // Insert an element into the Bloom Filter
 void insert(const std::string& element) {
 for (const auto& hashFunc : hashFunctions) {
 size_t index = hashFunc(element);
 bitArray[index] = true;
 }
 }

 // Check if an element is present in the Bloom Filter
 bool contains(const std::string& element) {
 for (const auto& hashFunc : hashFunctions) {
 size_t index = hashFunc(element);
 if (!bitArray[index]) {
 return false; // Definitely not present
 }
 }
 return true; // Might be present (false positive possible)
 }

 //Optional: Getters for array size and number of hash functions for testing/debugging
 size_t getArraySize() const { return arraySize; }
 size_t getNumHashFunctions() const { return numHashFunctions; }
};

int main() {
 // Example usage
 BloomFilter bf(1000, 3); // Create a Bloom Filter with size 1000 and 3 hash functions

 // Insert some elements
 bf.insert("apple");
 bf.insert("banana");
 bf.insert("cherry");

 // Check for elements
 std::cout << "'apple' is present: " << std::boolalpha << bf.contains("apple") << std::endl;   // Output: true (likely)
 std::cout << "'grape' is present: " << std::boolalpha << bf.contains("grape") << std::endl;   // Output: true or false (false positive possible)
 std::cout << "'banana' is present: " << std::boolalpha << bf.contains("banana") << std::endl;  // Output: true (likely)
 std::cout << "'cherry' is present: " << std::boolalpha << bf.contains("cherry") << std::endl;  // Output: true (likely)
 std::cout << "'date' is present: " << std::boolalpha << bf.contains("date") << std::endl;     // Output: true or false (false positive possible)

 return 0;
}

