// A C++ implementation of a greedy algorithm for the Fractional Knapsack problem
#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

// Structure to represent an item in the knapsack
struct Item {
    int value;
    int weight;
    double ratio; // value/weight
};

// Function to calculate the fractional knapsack value using a greedy approach
double fractionalKnapsack(int capacity, vector<Item>& items) {
    // Sort items based on value/weight ratio in descending order
    sort(items.begin(), items.end(), [](const Item& a, const Item& b) {
        return a.ratio > b.ratio;
    });

    double totalValue = 0.0;
    int currentWeight = 0;

    // Iterate through the sorted items
    for (const auto& item : items) {
        // If the entire item can be taken
        if (currentWeight + item.weight <= capacity) {
            currentWeight += item.weight;
            totalValue += item.value;
        } else {
            // Take a fraction of the item to fill the remaining capacity
            int remainingCapacity = capacity - currentWeight;
            totalValue += item.ratio * remainingCapacity;
            break; // Knapsack is full
        }
    }

    return totalValue;
}

int main() {
    int capacity = 50; // Knapsack capacity
    vector<Item> items = {
        {60, 10, 6.0}, // value, weight, ratio
        {100, 20, 5.0},
        {120, 30, 4.0}
    };

    double maxValue = fractionalKnapsack(capacity, items);

    cout << "Maximum value in knapsack = " << maxValue << endl;

    return 0;
}

