// C++ implementation of Activity Selection Problem
#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

// Structure to represent an activity
struct Activity {
    int start, finish;
};

// Function to sort activities based on finish time
bool compareActivities(Activity a, Activity b) {
    return (a.finish < b.finish);
}

// Function to find the maximum set of non-overlapping activities
vector<Activity> activitySelection(vector<Activity>& activities) {
    int n = activities.size();

    // Sort activities by finish time
    sort(activities.begin(), activities.end(), compareActivities);

    vector<Activity> selectedActivities;

    // The first activity is always selected
    selectedActivities.push_back(activities[0]);

    // Consider rest of the activities
    int lastFinishTime = activities[0].finish;

    for (int i = 1; i < n; i++) {
        // If this activity has start time greater than or equal to the finish time of previously
        // selected activity, then select it
        if (activities[i].start >= lastFinishTime) {
            selectedActivities.push_back(activities[i]);
            lastFinishTime = activities[i].finish;
        }
    }

    return selectedActivities;
}

int main() {
    vector<Activity> activities = {{
        {1, 2},
        {3, 4},
        {0, 6},
        {5, 7},
        {8, 9},
        {5, 9}
    }};

    vector<Activity> selected = activitySelection(activities);

    cout << "Selected activities are:\n";
    for (const auto& activity : selected) {
        cout << "(" << activity.start << ", " << activity.finish << ") ";
    }
    cout << endl;

    return 0;
}

huffman-coding.code:
cpp
#include <iostream>
#include <queue>
#include <map>
#include <vector>

using namespace std;

// Structure for a node in the Huffman tree
struct Node {
    char data;
    int frequency;
    Node* left;
    Node* right;

    Node(char data, int frequency) : data(data), frequency(frequency), left(nullptr), right(nullptr) {}
};

// Custom comparator for the priority queue to create a min-priority queue
struct CompareNodes {
    bool operator()(Node* a, Node* b) {
        return a->frequency > b->frequency;
    }
};

// Function to generate Huffman codes from the Huffman tree
void generateCodes(Node* root, string code, map<char, string>& huffmanCodes) {
    if (root == nullptr) {
        return;
    }

    if (root->data != '\0') { // If it's a leaf node (character node)
        huffmanCodes[root->data] = code;
    }

    generateCodes(root->left, code + "0", huffmanCodes);
    generateCodes(root->right, code + "1", huffmanCodes);
}

int main() {
    // Example input: Character frequencies
    map<char, int> frequencies = {
        {'a', 5},
        {'b', 9},
        {'c', 12},
        {'d', 13},
        {'e', 16},
        {'f', 45}
    };

    // Create a priority queue of nodes (min-priority queue)
    priority_queue<Node*, vector<Node*>, CompareNodes> pq;

    // Create a node for each character and push it into the priority queue
    for (auto const& [character, frequency] : frequencies) {
        pq.push(new Node(character, frequency));
    }

    // Build the Huffman tree
    while (pq.size() > 1) {
        // Extract the two nodes with the lowest frequencies
        Node* left = pq.top();
        pq.pop();
        Node* right = pq.top();
        pq.pop();

        // Create a new internal node with a frequency equal to the sum of the two nodes
        // The data is set to '\0' to indicate it's an internal node
        Node* internalNode = new Node('\0', left->frequency + right->frequency);
        internalNode->left = left;
        internalNode->right = right;

        // Push the internal node back into the priority queue
        pq.push(internalNode);
    }

    // The remaining node in the priority queue is the root of the Huffman tree
    Node* root = pq.top();
    pq.pop();

    // Generate Huffman codes from the Huffman tree
    map<char, string> huffmanCodes;
    generateCodes(root, "", huffmanCodes);

    // Print the Huffman codes
    cout << "Huffman Codes:" << endl;
    for (auto const& [character, code] : huffmanCodes) {
        cout << character << ": " << code << endl;
    }


    //Clean up allocated memory (Important to prevent memory leaks)
     function<void(Node*)> deleteTree = [&](Node* node) {
        if (node == nullptr) return;
        deleteTree(node->left);
        deleteTree(node->right);
        delete node;
    };

    deleteTree(root); //Delete entire tree starting from the root.

    return 0;
}

