 #include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

// Structure to represent an item with weight and value
struct Item {
    int value;
    int weight;
};

// Function to calculate the value-to-weight ratio
double valuePerWeight(Item item) {
    return (double)item.value / item.weight;
}

// Comparator function to sort items based on value-to-weight ratio in descending order
bool compareItems(Item a, Item b) {
    return valuePerWeight(a) > valuePerWeight(b);
}

// Function to solve the fractional knapsack problem
double fractionalKnapsack(int capacity, vector<Item>& items) {
    // Sort the items based on value-to-weight ratio
    sort(items.begin(), items.end(), compareItems);

    double totalValue = 0.0;  // Initialize the total value
    int currentWeight = 0;    // Initialize the current weight in the knapsack

    // Iterate through the sorted items
    for (Item item : items) {
        // If the item can be taken completely
        if (currentWeight + item.weight <= capacity) {
            currentWeight += item.weight;      // Add the item's weight to the knapsack
            totalValue += item.value;          // Add the item's value to the total value
        } else {
            // If the item can be taken partially
            int remainingCapacity = capacity - currentWeight; // Calculate remaining capacity
            totalValue += valuePerWeight(item) * remainingCapacity; // Add the fractional value
            break; // Knapsack is full
        }
    }

    return totalValue; // Return the maximum value
}

int main() {
    int capacity = 50; // Knapsack capacity
    vector<Item> items = {{
                                   60, 10
                               }, {
                                   100, 20
                               }, {
                                   120, 30
                               }};

    // Calculate the maximum value that can be put in the knapsack
    double maxValue = fractionalKnapsack(capacity, items);

    cout << "Maximum value: " << maxValue << endl; // Output the result

    return 0;
}

