#include <iostream>
#include <algorithm>
#include <vector>

using namespace std;

// Structure to represent a job
struct Job {
    char id;      // Job ID
    int deadline; // Deadline of the job
    int profit;   // Profit associated with the job
};

// Comparison function to sort jobs based on profit in decreasing order
bool compareJobs(Job a, Job b) {
    return (a.profit > b.profit);
}

// Function to perform job sequencing with deadlines
void jobSequencing(vector<Job>& jobs, int n) {
    // Sort jobs based on profit in decreasing order
    sort(jobs.begin(), jobs.end(), compareJobs);

    // Find the maximum deadline to determine the size of the schedule array
    int maxDeadline = 0;
    for (int i = 0; i < n; i++) {
        maxDeadline = max(maxDeadline, jobs[i].deadline);
    }

    // Initialize the schedule array with -1, indicating no job is scheduled
    vector<char> schedule(maxDeadline, ' ');

    // Iterate through the sorted jobs and schedule them
    int totalProfit = 0;
    for (int i = 0; i < n; i++) {
        // Find a free slot for the current job as late as possible before its deadline
        for (int j = min(maxDeadline - 1, jobs[i].deadline - 1); j >= 0; j--) {
            if (schedule[j] == ' ') {  // Check if the slot is free
                schedule[j] = jobs[i].id; // Schedule the job
                totalProfit += jobs[i].profit;
                break; // Job scheduled, move to the next job
            }
        }
    }

    // Print the scheduled jobs and total profit
    cout << "Scheduled Jobs: ";
    for (int i = 0; i < maxDeadline; i++) {
        if(schedule[i] != ' ')
          cout << schedule[i] << " ";
    }
    cout << endl;

    cout << "Total Profit: " << totalProfit << endl;
}

int main() {
    // Example usage
    vector<Job> jobs = {
        {'a', 2, 100},
        {'b', 1, 19},
        {'c', 2, 27},
        {'d', 1, 25},
        {'e', 3, 15}
    };

    int n = jobs.size();

    jobSequencing(jobs, n);

    return 0;
}

