// C++ implementation of Dijkstra's algorithm
#include <iostream>
#include <vector>
#include <queue>
#include <limits>

using namespace std;

// Structure to represent an edge in the graph
struct Edge {
    int to;
    int weight;
};

// Structure to represent a node in the graph
struct Node {
    int vertex;
    int distance;

    // Overload the less than operator for the priority queue
    bool operator>(const Node& other) const {
        return distance > other.distance;
    }
};

// Function to implement Dijkstra's algorithm
vector<int> dijkstra(const vector<vector<Edge>>& graph, int start) {
    int num_vertices = graph.size();
    vector<int> distances(num_vertices, numeric_limits<int>::max()); // Initialize distances to infinity
    distances[start] = 0; // Distance from start node to itself is 0

    priority_queue<Node, vector<Node>, greater<Node>> pq; // Min-priority queue
    pq.push({start, 0});

    while (!pq.empty()) {
        Node current = pq.top();
        pq.pop();

        int u = current.vertex;
        int dist_u = current.distance;

        // If we've already found a shorter path to u, skip
        if (dist_u > distances[u]) {
            continue;
        }

        // Iterate through the neighbors of u
        for (const Edge& edge : graph[u]) {
            int v = edge.to;
            int weight_uv = edge.weight;

            // If we find a shorter path to v through u
            if (distances[u] != numeric_limits<int>::max() && distances[u] + weight_uv < distances[v]) {
                distances[v] = distances[u] + weight_uv; // Update the distance to v
                pq.push({v, distances[v]}); // Add v to the priority queue
            }
        }
    }

    return distances;
}

int main() {
    // Example graph represented as an adjacency list
    // graph[i] is a vector of Edges representing the neighbors of vertex i
    vector<vector<Edge>> graph = {
        {{1, 4}, {2, 2}},
        {{0, 4}, {2, 5}, {3, 10}},
        {{0, 2}, {1, 5}, {3, 3}},
        {{1, 10}, {2, 3}}
    };

    int start_node = 0;

    vector<int> shortest_distances = dijkstra(graph, start_node);

    cout << "Shortest distances from node " << start_node << ":" << endl;
    for (int i = 0; i < shortest_distances.size(); ++i) {
        cout << "To node " << i << ": " << shortest_distances[i] << endl;
    }

    return 0;
}

