#include <iostream>
#include <vector>

using namespace std;

// Example: Fibonacci sequence using dynamic programming (memoization)
int fibonacci(int n, vector<int>& memo) {
    // Base cases
    if (n <= 1) {
        return n;
    }

    // Check if the result is already memoized
    if (memo[n] != -1) {
        return memo[n];
    }

    // Recursive call with memoization
    memo[n] = fibonacci(n - 1, memo) + fibonacci(n - 2, memo);
    return memo[n];
}

// Example: Fibonacci sequence using dynamic programming (tabulation)
int fibonacciTabulation(int n) {
    if (n <= 1) return n;

    vector<int> dp(n + 1);
    dp[0] = 0;
    dp[1] = 1;

    for (int i = 2; i <= n; ++i) {
        dp[i] = dp[i - 1] + dp[i - 2];
    }

    return dp[n];
}

int main() {
    int n = 10;

    // Memoization example
    vector<int> memo(n + 1, -1); // Initialize memoization table with -1
    cout << "Fibonacci(" << n << ") using memoization: " << fibonacci(n, memo) << endl;

    // Tabulation example
    cout << "Fibonacci(" << n << ") using tabulation: " << fibonacciTabulation(n) << endl;

    return 0;
}

