// C++ implementation of the Fibonacci sequence using iterative and matrix exponentiation approaches
#include <iostream>
#include <vector>

// Iterative approach - Efficient for larger n
unsigned long long fibonacciIterative(int n) {
    if (n <= 1) {
        return n;
    }
    unsigned long long a = 0, b = 1, temp;
    for (int i = 2; i <= n; ++i) {
        temp = a + b;
        a = b;
        b = temp;
    }
    return b;
}

// Matrix multiplication helper function
std::vector<std::vector<unsigned long long>> matrixMultiply(const std::vector<std::vector<unsigned long long>>& A, const std::vector<std::vector<unsigned long long>>& B) {
    std::vector<std::vector<unsigned long long>> C(2, std::vector<unsigned long long>(2, 0));
    for (int i = 0; i < 2; ++i) {
        for (int j = 0; j < 2; ++j) {
            for (int k = 0; k < 2; ++k) {
                C[i][j] += A[i][k] * B[k][j];
            }
        }
    }
    return C;
}

// Matrix exponentiation for Fibonacci - O(log n) time complexity
unsigned long long fibonacciMatrix(int n) {
    if (n <= 1) {
        return n;
    }

    std::vector<std::vector<unsigned long long>> result = {{1, 0}, {0, 1}}; // Identity matrix
    std::vector<std::vector<unsigned long long>> base = {{1, 1}, {1, 0}}; // Fibonacci matrix

    while (n > 0) {
        if (n % 2 == 1) {
            result = matrixMultiply(result, base);
        }
        base = matrixMultiply(base, base);
        n /= 2;
    }

    return result[0][1]; // F(n)
}

int main() {
    int n = 10; // Example: Calculate the 10th Fibonacci number

    std::cout << "Fibonacci(" << n << ") using iterative approach: " << fibonacciIterative(n) << std::endl;
    std::cout << "Fibonacci(" << n << ") using matrix exponentiation approach: " << fibonacciMatrix(n) << std::endl;

    return 0;
}

