#include <iostream>
#include <vector>
#include <algorithm>
#include <climits>

using namespace std;

// Function to find the minimum number of coins to make up the amount
int coinChange(vector<int>& coins, int amount) {
    // dp[i] will store the minimum number of coins needed to make up amount i
    vector<int> dp(amount + 1, amount + 1); // Initialize with a value greater than the maximum possible answer

    // Base case: 0 coins are needed to make up an amount of 0
    dp[0] = 0;

    // Iterate through all amounts from 1 to the target amount
    for (int i = 1; i <= amount; ++i) {
        // Iterate through all available coins
        for (int coin : coins) {
            // If the current coin is less than or equal to the current amount
            if (coin <= i) {
                // Update dp[i] with the minimum of its current value and
                // the number of coins needed to make up (i - coin) plus 1 (for the current coin)
                dp[i] = min(dp[i], dp[i - coin] + 1);
            }
        }
    }

    // If dp[amount] is still greater than the amount, it means no solution was found
    return dp[amount] > amount ? -1 : dp[amount];
}

int main() {
    vector<int> coins = {1, 2, 5}; // Example coin denominations
    int amount = 11;               // Example target amount

    int minCoins = coinChange(coins, amount);

    if (minCoins == -1) {
        cout << "Cannot make the amount with the given coins." << endl;
    } else {
        cout << "Minimum number of coins needed: " << minCoins << endl;
    }

    return 0;
}

