// C++ implementation of finding the Longest Increasing Subsequence (LIS)
#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

// Function to find the length of the LIS
int longestIncreasingSubsequence(const vector<int>& nums) {
    // tails[i] is the smallest tail of all increasing subsequences with length i+1.
    vector<int> tails;

    // Iterate through each number in the input sequence
    for (int num : nums) {
        // If we find a number in tails that is greater than or equal to the current number,
        // it means we can potentially improve an existing subsequence.
        auto it = lower_bound(tails.begin(), tails.end(), num);

        // If the lower_bound is at the end, it means the current number is greater than all elements in 'tails',
        // thus extending the longest increasing subsequence found so far.
        if (it == tails.end()) {
            tails.push_back(num);
        } else {
            // Otherwise, replace the smallest number >= current number with the current number.
            // This ensures we keep the smallest tail for each subsequence length.
            *it = num;
        }
    }

    // The size of the 'tails' vector is the length of the LIS.
    return tails.size();
}

int main() {
    vector<int> nums = {10, 9, 2, 5, 3, 7, 101, 18}; // Example input
    int lisLength = longestIncreasingSubsequence(nums);

    cout << "Length of the Longest Increasing Subsequence: " << lisLength << endl; // Output the result

    vector<int> nums2 = {0,1,0,3,2,3}; // Another Example Input
    lisLength = longestIncreasingSubsequence(nums2);
    cout << "Length of the Longest Increasing Subsequence: " << lisLength << endl;
    return 0;
}

