#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

// Function to find the length of the Longest Common Subsequence (LCS)
string longestCommonSubsequence(string s1, string s2) {
    int m = s1.length();
    int n = s2.length();

    // Create a 2D vector to store lengths of LCS for subproblems
    vector<vector<int>> dp(m + 1, vector<int>(n + 1, 0));

    // Fill the dp table in bottom-up manner
    for (int i = 1; i <= m; ++i) {
        for (int j = 1; j <= n; ++j) {
            if (s1[i - 1] == s2[j - 1]) {
                // If characters match, increment LCS length by 1
                dp[i][j] = dp[i - 1][j - 1] + 1;
            } else {
                // If characters don't match, take the maximum of LCS lengths from adjacent cells
                dp[i][j] = max(dp[i - 1][j], dp[i][j - 1]);
            }
        }
    }

    // Construct the LCS string by backtracking through the dp table
    int i = m, j = n;
    string lcs = "";
    while (i > 0 && j > 0) {
        if (s1[i - 1] == s2[j - 1]) {
            // If characters match, include the character in the LCS and move diagonally
            lcs = s1[i - 1] + lcs;
            i--;
            j--;
        } else if (dp[i - 1][j] > dp[i][j - 1]) {
            // If LCS length is greater by moving up, move up
            i--;
        } else {
            // If LCS length is greater by moving left, move left
            j--;
        }
    }

    return lcs;
}

int main() {
    string s1 = "AGGTAB";
    string s2 = "GXTXAYB";

    string lcs = longestCommonSubsequence(s1, s2);

    cout << "Longest Common Subsequence is: " << lcs << endl;

    return 0;
}

