// C++ implementation of Edit Distance
#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

// Function to calculate the edit distance between two strings
int editDistance(const string& str1, const string& str2) {
  int m = str1.length();
  int n = str2.length();

  // Create a DP table to store the edit distances
  vector<vector<int>> dp(m + 1, vector<int>(n + 1, 0));

  // Initialize the first row and column of the DP table
  for (int i = 0; i <= m; ++i) {
    dp[i][0] = i; // Cost of deleting i characters from str1 to get an empty string
  }
  for (int j = 0; j <= n; ++j) {
    dp[0][j] = j; // Cost of inserting j characters into str1 to get str2
  }

  // Populate the DP table using dynamic programming
  for (int i = 1; i <= m; ++i) {
    for (int j = 1; j <= n; ++j) {
      if (str1[i - 1] == str2[j - 1]) {
        // If the characters match, no cost
        dp[i][j] = dp[i - 1][j - 1];
      } else {
        // If the characters don't match, consider insertion, deletion, and substitution
        dp[i][j] = 1 + min({dp[i - 1][j],   // Deletion
                             dp[i][j - 1],   // Insertion
                             dp[i - 1][j - 1]}); // Substitution
      }
    }
  }

  // The edit distance is stored in dp[m][n]
  return dp[m][n];
}

int main() {
  string str1 = "kitten";
  string str2 = "sitting";

