// Matrix Chain Multiplication using Dynamic Programming
#include <iostream>
#include <vector>
#include <limits>

using namespace std;

// Function to calculate the minimum number of scalar multiplications
int matrixChainOrder(vector<int>& dimensions, int n) {
    // cost[i][j] = Minimum number of scalar multiplications needed
    // to compute the matrix A[i]A[i+1]...A[j] = A[i..j]
    vector<vector<int>> cost(n, vector<int>(n, 0));

    // For chain length l = 2 to n
    for (int len = 2; len < n; len++) {
        for (int i = 1; i < n - len + 1; i++) {
            int j = i + len - 1;
            cost[i][j] = numeric_limits<int>::max();

            // Try all possible places to split the matrix chain
            for (int k = i; k < j; k++) {
                // cost[i][k] + cost[k+1][j] + dimensions[i-1]*dimensions[k]*dimensions[j]
                int q = cost[i][k] + cost[k + 1][j] + dimensions[i - 1] * dimensions[k] * dimensions[j];
                if (q < cost[i][j]) {
                    cost[i][j] = q;
                }
            }
        }
    }

    // Return the minimum cost to multiply the entire matrix chain
    return cost[1][n - 1];
}

int main() {
    // Example usage:
    // Consider matrices A, B, C, D of dimensions 40x20, 20x30, 30x10, and 10x30 respectively
    vector<int> dimensions = {40, 20, 30, 10, 30}; // Dimensions of matrices: A(40x20), B(20x30), C(30x10), D(10x30)
    int n = dimensions.size(); // n = number of matrices + 1

    cout << "Minimum number of scalar multiplications needed: " << matrixChainOrder(dimensions, n) << endl; // Output: 30000

    return 0;
}

