 #include <iostream>
#include <vector>

using namespace std;

// Function to solve the subset sum problem using dynamic programming
bool isSubsetSum(const vector<int>& set, int n, int sum) {
    // Create a boolean table to store results of subproblems
    vector<vector<bool>> dp(n + 1, vector<bool>(sum + 1, false));

    // If sum is 0, then answer is true
    for (int i = 0; i <= n; i++)
        dp[i][0] = true;

    // If sum is not 0 and set is empty, then answer is false
    for (int i = 1; i <= sum; i++)
        dp[0][i] = false;

    // Fill the dp table in bottom up manner
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= sum; j++) {
            if (set[i - 1] <= j) {
                dp[i][j] = dp[i - 1][j] || dp[i - 1][j - set[i - 1]];
            } else {
                dp[i][j] = dp[i - 1][j];
            }
        }
    }

    return dp[n][sum];
}

int main() {
    vector<int> set = {3, 34, 4, 12, 5, 2};
    int sum = 9;
    int n = set.size();

    if (isSubsetSum(set, n, sum) == true)
        cout << "Found a subset with given sum";
    else
        cout << "No subset with given sum";
    cout << endl;

    //Example with empty set and target sum 0
    vector<int> empty_set = {};
    int sum2 = 0;
    int n2 = empty_set.size();

    if (isSubsetSum(empty_set, n2, sum2) == true)
        cout << "Found a subset with given sum (empty set, sum 0)";
    else
        cout << "No subset with given sum (empty set, sum 0)";
    cout << endl;

    // Example with target sum that is not possible
    vector<int> set3 = {1,2,3,4,5};
    int sum3 = 20; // No subset will sum to 20 in this set
    int n3 = set3.size();
    if (isSubsetSum(set3, n3, sum3) == true)
        cout << "Found a subset with given sum (target sum impossible)";
    else
        cout << "No subset with given sum (target sum impossible)";
    cout << endl;

    return 0;
}

