// Dynamic Programming on Trees - Maximum Independent Set Problem
#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

// Function to solve the Maximum Independent Set problem on a tree using DP
pair<int, int> maxIndependentSet(int node, int parent, const vector<vector<int>>& adj, vector<pair<int, int>>& dp) {
    // dp[node].first stores the size of the maximum independent set including the node
    // dp[node].second stores the size of the maximum independent set excluding the node

    // Base case: If DP values are already computed, return them
    if (dp[node].first != -1) {
        return dp[node];
    }

    // Initialize the DP values
    int includeNode = 1; // Initially, include the current node
    int excludeNode = 0; // Initially, exclude the current node

    // Iterate over all children of the current node
    for (int child : adj[node]) {
        if (child != parent) {
            // Recursively calculate the maximum independent set for the child
            pair<int, int> childResult = maxIndependentSet(child, node, adj, dp);

            // If we include the current node, we cannot include any of its children
            includeNode += childResult.second;

            // If we exclude the current node, we can either include or exclude its children
            excludeNode += max(childResult.first, childResult.second);
        }
    }

    // Store the computed DP values
    dp[node] = {includeNode, excludeNode};
    return dp[node];
}

int main() {
    int n; // Number of nodes in the tree
    cout << "Enter the number of nodes: ";
    cin >> n;

    vector<vector<int>> adj(n + 1); // Adjacency list to represent the tree
    vector<pair<int, int>> dp(n + 1, {-1, -1}); // DP table to store the maximum independent set size

    // Input the edges of the tree
    cout << "Enter the edges (u v), one edge per line (e.g., 1 2):\n";
    for (int i = 0; i < n - 1; ++i) {
        int u, v;
        cin >> u >> v;
        adj[u].push_back(v);
        adj[v].push_back(u);
    }

    // Calculate the maximum independent set starting from node 1 (arbitrary root)
    pair<int, int> result = maxIndependentSet(1, 0, adj, dp);

    // The size of the maximum independent set is the maximum of including or excluding the root
    cout << "Size of the Maximum Independent Set: " << max(result.first, result.second) << endl;

    return 0;
}

