// C++ implementation of a simple backtracking algorithm: Sudoku Solver
#include <iostream>
#include <vector>

using namespace std;

// Function to print the Sudoku grid
void printGrid(const vector<vector<int>>& grid) {
    for (int i = 0; i < 9; ++i) {
        for (int j = 0; j < 9; ++j) {
            cout << grid[i][j] << " ";
        }
        cout << endl;
    }
}

// Function to check if it's safe to place a number in a given cell
bool isSafe(const vector<vector<int>>& grid, int row, int col, int num) {
    // Check row
    for (int x = 0; x < 9; ++x)
        if (grid[row][x] == num)
            return false;

    // Check column
    for (int x = 0; x < 9; ++x)
        if (grid[x][col] == num)
            return false;

    // Check 3x3 box
    int startRow = row - row % 3;
    int startCol = col - col % 3;

    for (int i = 0; i < 3; ++i)
        for (int j = 0; j < 3; ++j)
            if (grid[i + startRow][j + startCol] == num)
                return false;

    return true;
}

// Recursive function to solve the Sudoku
bool solveSudoku(vector<vector<int>>& grid) {
    int row, col;

    // Find an unassigned cell. If no unassigned cell is found, Sudoku is solved.
    bool isEmpty = false;
    for (row = 0; row < 9; ++row) {
        for (col = 0; col < 9; ++col) {
            if (grid[row][col] == 0) {
                isEmpty = true;
                break;
            }
        }
        if (isEmpty)
            break;
    }

    if (!isEmpty) // No unassigned cell means solved
        return true;

    // Try digits 1 to 9
    for (int num = 1; num <= 9; ++num) {
        if (isSafe(grid, row, col, num)) {
            grid[row][col] = num;

            // Recursively try to solve the rest of the Sudoku
            if (solveSudoku(grid))
                return true;

            // If the current assignment leads to a dead end, backtrack
            grid[row][col] = 0; // Reset to 0 (unassigned)
        }
    }

    // If no digit leads to a solution, backtrack
    return false;
}

int main() {
    // Example Sudoku grid (0 represents unassigned cells)
    vector<vector<int>> grid = {
        {5, 3, 0, 0, 7, 0, 0, 0, 0},
        {6, 0, 0, 1, 9, 5, 0, 0, 0},
        {0, 9, 8, 0, 0, 0, 0, 6, 0},
        {8, 0, 0, 0, 6, 0, 0, 0, 3},
        {4, 0, 0, 8, 0, 3, 0, 0, 1},
        {7, 0, 0, 0, 2, 0, 0, 0, 6},
        {0, 6, 0, 0, 0, 0, 2, 8, 0},
        {0, 0, 0, 4, 1, 9, 0, 0, 5},
        {0, 0, 0, 0, 8, 0, 0, 7, 9}
    };

    cout << "Sudoku Puzzle:\n";
    printGrid(grid);

    if (solveSudoku(grid)) {
        cout << "\nSudoku Solution:\n";
        printGrid(grid);
    } else {
        cout << "\nNo solution exists.\n";
    }

    return 0;
}

