// C++ program to generate all permutations of a given string/array
#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

// Function to print a vector of integers (helper function for demonstration)
void printVector(const vector<int>& arr) {
    for (int num : arr) {
        cout << num << " ";
    }
    cout << endl;
}

// Function to generate permutations of a vector of integers
void generatePermutations(vector<int>& arr, int l, int r, vector<vector<int>>& result) {
    // Base case: when l == r, we have a complete permutation
    if (l == r) {
        result.push_back(arr);
        //Optional: printVector(arr);  // Print the current permutation
    } else {
        // Iterate through the remaining elements in the array
        for (int i = l; i <= r; i++) {
            // Swap the current element with the element at index l
            swap(arr[l], arr[i]);

            // Recursively generate permutations for the subarray arr[l+1...r]
            generatePermutations(arr, l + 1, r, result);

            // Backtrack: swap the elements back to restore the original order
            swap(arr[l], arr[i]);
        }
    }
}


// Function to generate permutations using the next_permutation STL function.
vector<vector<int>> generatePermutationsSTL(vector<int> arr) {
    sort(arr.begin(), arr.end()); //Ensure the input is sorted to generate all permutations.
    vector<vector<int>> result;

    do {
        result.push_back(arr);
    } while (next_permutation(arr.begin(), arr.end()));

    return result;
}


int main() {
    vector<int> numbers = {1, 2, 3};
    vector<vector<int>> permutations;

    cout << "Generating permutations using recursive backtracking:\n";
    generatePermutations(numbers, 0, numbers.size() - 1, permutations);

    cout << "Total number of permutations: " << permutations.size() << endl;
    cout << "\nGenerating permutations using STL next_permutation:\n";

    vector<vector<int>> permutationsSTL = generatePermutationsSTL(numbers);

     for (const auto& perm : permutationsSTL) {
            for (int num : perm) {
                cout << num << " ";
            }
            cout << endl;
        }


    cout << "Total number of permutations using STL: " << permutationsSTL.size() << endl;

    return 0;
}

