// C++ program to generate power set of a set
#include <iostream>
#include <vector>
#include <cmath>

using namespace std;

// Function to generate power set of a set
vector<vector<int>> powerSet(vector<int>& nums) {
    int n = nums.size();
    int powerSetSize = pow(2, n);
    vector<vector<int>> result;

    // Run loop 2^n times (total number of subsets)
    for (int counter = 0; counter < powerSetSize; counter++) {
        vector<int> subset;
        for (int j = 0; j < n; j++) {
            // Check if jth bit in the counter is set
            if (counter & (1 << j)) {
                subset.push_back(nums[j]);
            }
        }
        result.push_back(subset);
    }

    return result;
}

int main() {
    vector<int> nums = {1, 2, 3};
    vector<vector<int>> subsets = powerSet(nums);

    cout << "Power Set:" << endl;
    for (auto subset : subsets) {
        cout << "{";
        for (size_t i = 0; i < subset.size(); ++i) {
            cout << subset[i];
            if (i < subset.size() - 1) {
                cout << ", ";
            }
        }
        cout << "} ";
    }
    cout << endl;

    return 0;
}

