#include <iostream>
#include <vector>

using namespace std;

// Function to check if a queen can be placed at board[row][col]
bool isSafe(vector<vector<bool>>& board, int row, int col, int n) {
    // Check this row on the left side
    for (int i = 0; i < col; i++) {
        if (board[row][i]) {
            return false;
        }
    }

    // Check upper diagonal on the left side
    for (int i = row, j = col; i >= 0 && j >= 0; i--, j--) {
        if (board[i][j]) {
            return false;
        }
    }

    // Check lower diagonal on the left side
    for (int i = row, j = col; i < n && j >= 0; i++, j--) {
        if (board[i][j]) {
            return false;
        }
    }

    return true;
}

// Recursive function to solve N Queen problem
bool solveNQueensUtil(vector<vector<bool>>& board, int col, int n) {
    // Base case: If all queens are placed then return true
    if (col >= n) {
        return true;
    }

    // Consider this column and try placing this queen in all rows one by one
    for (int i = 0; i < n; i++) {
        // Check if the queen can be placed on board[i][col]
        if (isSafe(board, i, col, n)) {
            // Place this queen in board[i][col]
            board[i][col] = true;

            // Recur to place rest of the queens
            if (solveNQueensUtil(board, col + 1, n)) {
                return true;
            }

            // If placing queen in board[i][col] doesn't lead to a solution, then remove queen from board[i][col] (Backtrack)
            board[i][col] = false; // BACKTRACK
        }
    }

    // If the queen cannot be placed in any row in this column col, then return false
    return false;
}

// This function solves the N Queen problem using Backtracking.  It mainly uses solveNQueensUtil() to solve the problem.
vector<vector<string>> solveNQueens(int n) {
    vector<vector<bool>> board(n, vector<bool>(n, false));
    vector<vector<string>> solutions;

    if (solveNQueensUtil(board, 0, n) == false) {
        return solutions; // No solution exists
    }

    // Convert boolean board to string format for solutions
    vector<string> solution;
    for (int i = 0; i < n; ++i) {
        string row_str = "";
        for (int j = 0; j < n; ++j) {
            if (board[i][j]) {
                row_str += 'Q';
            } else {
                row_str += '.';
            }
        }
        solution.push_back(row_str);
    }
    solutions.push_back(solution);

    //Printing only one solution. For all solutions, need to modify solveNQueensUtil to collect all possible board states.
    return solutions;
}

int main() {
    int n = 4; // Example: Solve for a 4x4 board
    vector<vector<string>> solutions = solveNQueens(n);

    if (solutions.empty()) {
        cout << "No solutions exist for N = " << n << endl;
    } else {
        cout << "Solution for N = " << n << ":" << endl;
        for (const auto& solution : solutions) {
            for (const string& row : solution) {
                cout << row << endl;
            }
            cout << endl;
        }
    }

    return 0;
}

