#include <iostream>
#include <vector>

using namespace std;

// Function to print the Sudoku grid
void printGrid(const vector<vector<int>>& grid) {
    for (int i = 0; i < 9; ++i) {
        for (int j = 0; j < 9; ++j) {
            cout << grid[i][j] << " ";
        }
        cout << endl;
    }
}

// Function to check if a number can be placed at a given position
bool isValid(const vector<vector<int>>& grid, int row, int col, int num) {
    // Check if the number is already present in the row
    for (int i = 0; i < 9; ++i) {
        if (grid[row][i] == num) {
            return false;
        }
    }

    // Check if the number is already present in the column
    for (int i = 0; i < 9; ++i) {
        if (grid[i][col] == num) {
            return false;
        }
    }

    // Check if the number is already present in the 3x3 subgrid
    int startRow = row - row % 3;
    int startCol = col - col % 3;
    for (int i = 0; i < 3; ++i) {
        for (int j = 0; j < 3; ++j) {
            if (grid[startRow + i][startCol + j] == num) {
                return false;
            }
        }
    }

    return true;
}

// Function to find an empty cell in the grid
bool findEmptyCell(const vector<vector<int>>& grid, int& row, int& col) {
    for (row = 0; row < 9; ++row) {
        for (col = 0; col < 9; ++col) {
            if (grid[row][col] == 0) {
                return true;
            }
        }
    }
    return false;
}

// Backtracking function to solve the Sudoku
bool solveSudoku(vector<vector<int>>& grid) {
    int row, col;

    // If there are no empty cells, the Sudoku is solved
    if (!findEmptyCell(grid, row, col)) {
        return true; // Sudoku solved
    }

    // Try placing digits from 1 to 9 in the empty cell
    for (int num = 1; num <= 9; ++num) {
        if (isValid(grid, row, col, num)) {
            // Place the number if it's valid
            grid[row][col] = num;

            // Recursively try to solve the rest of the Sudoku
            if (solveSudoku(grid)) {
                return true; // Solution found
            }

            // If the recursive call fails, backtrack and try a different number
            grid[row][col] = 0; // Reset the cell
        }
    }

    // No number can be placed in this cell, so backtrack
    return false; // Trigger backtracking
}

int main() {
    // Example Sudoku grid (0 represents empty cells)
    vector<vector<int>> grid = {
        {5, 3, 0, 0, 7, 0, 0, 0, 0},
        {6, 0, 0, 1, 9, 5, 0, 0, 0},
        {0, 9, 8, 0, 0, 0, 0, 6, 0},
        {8, 0, 0, 0, 6, 0, 0, 0, 3},
        {4, 0, 0, 8, 0, 3, 0, 0, 1},
        {7, 0, 0, 0, 2, 0, 0, 0, 6},
        {0, 6, 0, 0, 0, 0, 2, 8, 0},
        {0, 0, 0, 4, 1, 9, 0, 0, 5},
        {0, 0, 0, 0, 8, 0, 0, 7, 9}
    };

    cout << "Sudoku grid before solving:\n";
    printGrid(grid);

    if (solveSudoku(grid)) {
        cout << "\nSudoku grid after solving:\n";
        printGrid(grid);
    } else {
        cout << "\nNo solution exists.\n";
    }

    return 0;
}

