// C++ implementation of Rat in a Maze problem using backtracking
#include <iostream>
#include <vector>

using namespace std;

// Function to check if a cell (x, y) is safe to move to
bool isSafe(int x, int y, const vector<vector<int>>& maze, const vector<vector<int>>& sol)
{
    int n = maze.size();
    return (x >= 0 && x < n && y >= 0 && y < n && maze[x][y] == 1 && sol[x][y] == 0);
}

// Recursive function to solve the Rat in a Maze problem
bool solveMazeUtil(const vector<vector<int>>& maze, int x, int y, vector<vector<int>>& sol)
{
    int n = maze.size();

    // If (x, y) is the destination return true
    if (x == n - 1 && y == n - 1 && maze[x][y] == 1)
    {
        sol[x][y] = 1;
        return true;
    }

    // Check if maze[x][y] is valid
    if (isSafe(x, y, maze, sol))
    {
        // Mark x, y as part of solution path
        sol[x][y] = 1;

        // Move forward in x direction
        if (solveMazeUtil(maze, x + 1, y, sol))
            return true;

        // If moving in x direction doesn't give solution
        // then Move forward in y direction
        if (solveMazeUtil(maze, x, y + 1, sol))
            return true;

        // If none of the above movements work then BACKTRACK:
        // Unmark x, y as part of solution path
        sol[x][y] = 0;
        return false;
    }

    return false;
}

// Function to solve the Rat in a Maze problem
bool solveMaze(vector<vector<int>>& maze)
{
    int n = maze.size();
    vector<vector<int>> sol(n, vector<int>(n, 0)); // Initialize solution matrix with all 0s

    if (solveMazeUtil(maze, 0, 0, sol) == false)
    {
        cout << "Solution doesn't exist" << endl;
        return false;
    }

    // Print the solution matrix
    cout << "Solution:" << endl;
    for (int i = 0; i < n; i++)
    {
        for (int j = 0; j < n; j++)
            cout << " " << sol[i][j] << " ";
        cout << endl;
    }
    return true;
}

int main()
{
    vector<vector<int>> maze = {{
        1, 0, 0, 0},
        {1, 1, 0, 1},
        {0, 1, 0, 0},
        {1, 1, 1, 1
    }};

    solveMaze(maze);

    return 0;
}

