#include <iostream>
#include <vector>
#include <queue>
#include <limits>

using namespace std;

// Structure to represent an edge in the graph
struct Edge {
    int destination;
    int weight;
};

// Function to perform Dijkstra's algorithm to find the shortest paths from a source vertex to all other vertices
vector<int> dijkstra(const vector<vector<Edge>>& graph, int source) {
    int numVertices = graph.size();
    vector<int> distance(numVertices, numeric_limits<int>::max()); // Initialize distances to infinity
    vector<bool> visited(numVertices, false);

    distance[source] = 0; // Distance from source to itself is 0

    priority_queue<pair<int, int>, vector<pair<int, int>>, greater<pair<int, int>>> pq; // Min-priority queue
    pq.push({0, source}); // {distance, vertex}

    while (!pq.empty()) {
        int u = pq.top().second; // Vertex with the smallest distance
        pq.pop();

        if (visited[u]) continue; // Skip if already visited
        visited[u] = true;

        for (const Edge& edge : graph[u]) {
            int v = edge.destination; // Neighboring vertex
            int weight = edge.weight; // Weight of the edge

            if (distance[u] != numeric_limits<int>::max() && distance[u] + weight < distance[v]) {
                distance[v] = distance[u] + weight; // Update distance
                pq.push({distance[v], v}); // Add to the priority queue
            }
        }
    }

    return distance;
}

int main() {
    // Example Graph (Adjacency List Representation)
    int numVertices = 6;  // Number of vertices in the graph
    vector<vector<Edge>> graph(numVertices);

    // Adding edges to the graph (u -> v, weight)
    graph[0].push_back({1, 4});
    graph[0].push_back({2, 2});
    graph[1].push_back({2, 5});
    graph[1].push_back({3, 10});
    graph[2].push_back({4, 3});
    graph[3].push_back({5, 11});
    graph[4].push_back({3, 4});
    graph[4].push_back({5, 1});

    // Source vertex for Dijkstra's algorithm
    int source = 0;

    // Calculate shortest paths from source using Dijkstra's algorithm
    vector<int> shortestDistances = dijkstra(graph, source);

    // Print the shortest distances
    cout << "Shortest distances from vertex " << source << ":" << endl;
    for (int i = 0; i < numVertices; ++i) {
        cout << "Vertex " << i << ": ";
        if (shortestDistances[i] == numeric_limits<int>::max()) {
            cout << "Infinity" << endl;
        } else {
            cout << shortestDistances[i] << endl;
        }
    }

    return 0;
}

