// C++ implementation of BFS and DFS for graph traversal
#include <iostream>
#include <vector>
#include <queue>
#include <stack>

using namespace std;

// Adjacency list representation of the graph
vector<vector<int>> adj;
vector<bool> visited;

// BFS traversal
void bfs(int start_node) {
  queue<int> q;
  visited.assign(adj.size(), false);

  q.push(start_node);
  visited[start_node] = true;

  cout << "BFS Traversal: ";
  while (!q.empty()) {
    int u = q.front();
    q.pop();
    cout << u << " ";

    for (int v : adj[u]) {
      if (!visited[v]) {
        visited[v] = true;
        q.push(v);
      }
    }
  }
  cout << endl;
}

// DFS traversal (recursive)
void dfs(int u) {
  visited[u] = true;
  cout << u << " ";

  for (int v : adj[u]) {
    if (!visited[v]) {
      dfs(v);
    }
  }
}

// DFS traversal (iterative)
void iterative_dfs(int start_node) {
  stack<int> s;
  visited.assign(adj.size(), false);

  s.push(start_node);

  cout << "Iterative DFS Traversal: ";
  while (!s.empty()) {
    int u = s.top();
    s.pop();

    if (!visited[u]) {
      cout << u << " ";
      visited[u] = true;
    }

    // Push neighbors in reverse order to maintain the order of traversal
    for (int i = adj[u].size() - 1; i >= 0; --i) {
      int v = adj[u][i];
      if (!visited[v]) {
        s.push(v);
      }
    }
  }
  cout << endl;
}

int main() {
  int num_vertices = 6;
  adj.resize(num_vertices);

  // Example graph (adjacency list)
  adj[0] = {1, 2};
  adj[1] = {0, 3, 4};
  adj[2] = {0, 4};
  adj[3] = {1, 5};
  adj[4] = {1, 2, 5};
  adj[5] = {3, 4};

  // Perform BFS starting from vertex 0
  bfs(0);

  // Perform DFS starting from vertex 0
  visited.assign(adj.size(), false);
  cout << "Recursive DFS Traversal: ";
  dfs(0);
  cout << endl;

  // Perform iterative DFS starting from vertex 0
  iterative_dfs(0);

  return 0;
}

