#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

// Structure to represent an edge in the graph
struct Edge {
    int src, dest, weight;
};

// Function to find the parent of a node in the disjoint-set data structure
int find(vector<int>& parent, int i) {
    if (parent[i] == i)
        return i;
    return parent[i] = find(parent, parent[i]); // Path compression
}

// Function to perform union of two sets
void unionSets(vector<int>& parent, vector<int>& rank, int x, int y) {
    int xroot = find(parent, x);
    int yroot = find(parent, y);

    // Attach smaller rank tree under root of high rank tree
    // (Union by Rank)
    if (rank[xroot] < rank[yroot])
        parent[xroot] = yroot;
    else if (rank[xroot] > rank[yroot])
        parent[yroot] = xroot;
    else {
        parent[yroot] = xroot;
        rank[xroot]++;
    }
}

// Kruskal's algorithm to find the Minimum Spanning Tree
vector<Edge> kruskalMST(vector<Edge>& edges, int numVertices) {
    vector<Edge> mst;
    // Sort edges by weight in ascending order
    sort(edges.begin(), edges.end(), [](const Edge& a, const Edge& b) {
        return a.weight < b.weight;
    });

    // Initialize disjoint-set data structure
    vector<int> parent(numVertices);
    vector<int> rank(numVertices, 0);
    for (int i = 0; i < numVertices; i++)
        parent[i] = i;

    // Iterate through sorted edges
    for (const Edge& edge : edges) {
        int rootSrc = find(parent, edge.src);
        int rootDest = find(parent, edge.dest);

        // If including this edge doesn't cause a cycle, include it in MST
        if (rootSrc != rootDest) {
            mst.push_back(edge);
            unionSets(parent, rank, rootSrc, rootDest);
        }
    }

    return mst;
}

int main() {
    int numVertices = 4;
    vector<Edge> edges = {
        {0, 1, 10},
        {0, 2, 6},
        {0, 3, 5},
        {1, 3, 15},
        {2, 3, 4}
    };

    vector<Edge> mst = kruskalMST(edges, numVertices);

    cout << "Edges in MST:\n";
    int totalWeight = 0;
    for (const Edge& edge : mst) {
        cout << edge.src << " - " << edge.dest << " : " << edge.weight << endl;
        totalWeight += edge.weight;
    }

    cout << "Total weight of MST: " << totalWeight << endl;

    return 0;
}

